import React, { useState, useEffect } from 'react';

export default function MonCompte() {
  const [userData, setUserData] = useState({ nom: '', prenom: '', email: '' });
  const [editedData, setEditedData] = useState({ nom: '', prenom: '', email: '' });
  const [isEditing, setIsEditing] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    fetch('/profile', { method: 'GET', credentials: 'include' })
      .then((res) => {
        if (!res.ok) throw new Error('Erreur chargement profil');
        return res.json();
      })
      .then((data) => {
        setUserData(data);
        setEditedData(data);
      })
      .catch(() => setError('Impossible de charger le profil utilisateur.'));
  }, []);

  function handleChange(e) {
    setEditedData({ ...editedData, [e.target.name]: e.target.value });
  }

  function handleCancel() {
    setEditedData(userData);
    setIsEditing(false);
    setError('');
  }

  function handleConfirm() {
    fetch('/profile', {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      credentials: 'include',
      body: JSON.stringify(editedData),
    })
      .then((res) => {
        if (!res.ok) throw new Error('Erreur mise à jour profil');
        return res.json();
      })
      .then(() => {
        setUserData(editedData);
        setIsEditing(false);
        setError('');
      })
      .catch(() => setError('Erreur lors de la mise à jour.'));
  }

  return (
    <div>
      {error && <p>{error}</p>}

      <div>
        <label>
          Nom :
          <input
            name="nom"
            value={editedData.nom}
            onChange={handleChange}
            disabled={!isEditing}
          />
        </label>
      </div>

      <div>
        <label>
          Prénom :
          <input
            name="prenom"
            value={editedData.prenom}
            onChange={handleChange}
            disabled={!isEditing}
          />
        </label>
      </div>

      <div>
        <label>
          Email :
          <input
            name="email"
            value={editedData.email}
            onChange={handleChange}
            disabled={!isEditing}
          />
        </label>
      </div>

      {!isEditing && (
        <button onClick={() => setIsEditing(true)}>Modifier</button>
      )}
      {isEditing && (
        <>
          <button onClick={handleCancel}>Annuler les modifications</button>
          <button onClick={handleConfirm}>Confirmer</button>
        </>
      )}
    </div>
  );
}
