import React from 'react';
import { Outlet, NavLink } from 'react-router-dom';
import { FiHome, FiHelpCircle, FiUpload, FiClock, FiUser } from 'react-icons/fi';
import Topbar from '../components/Topbar';

function DashboardUserLayout() {
  const linkClasses = ({ isActive }) =>
    `flex items-center gap-2 px-4 py-2 rounded-lg text-sm font-medium transition-all ${
      isActive
        ? 'bg-green-600 text-white shadow'
        : 'text-gray-700 hover:bg-gray-200'
    }`;

  return (
    <div className="flex min-h-screen bg-gray-100 font-sans flex-col">
      <Topbar userName="César" />
      <div className="flex flex-1">
        {/* Sidebar */}
        <aside className="w-64 bg-white shadow-lg p-4">
          <h1 className="text-2xl font-bold mb-8 text-green-700 text-center">Dashboard</h1>
          <nav className="space-y-3">
            <NavLink to="/" end className={linkClasses}>
              <FiHome className="text-lg" /> Accueil
            </NavLink>
            <NavLink to="/comment-ca-marche" className={linkClasses}>
              <FiHelpCircle className="text-lg" /> Comment ça marche
            </NavLink>
            <NavLink to="/importer" className={linkClasses}>
              <FiUpload className="text-lg" /> Importer des données
            </NavLink>
            <NavLink to="/historique" className={linkClasses}>
              <FiClock className="text-lg" /> Historique
            </NavLink>
            <NavLink to="/mon-compte" className={linkClasses}>
              <FiUser className="text-lg" /> Mon compte
            </NavLink>
          </nav>
        </aside>

        {/* Contenu principal */}
        <main className="flex-1 p-8">
          <Outlet />
        </main>
      </div>
    </div>
  );
}

export default DashboardUserLayout;
