import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

import DashboardUserLayout from './layouts/DashboardUserLayout';
import Home from './pages/Home';
import CommentCaMarche from './pages/CommentCaMarche';
import ImporterDonnees from './pages/ImporterDonnees';
import Historique from './pages/Historique';
import MonCompte from './pages/MonCompte';

import SignIn from './pages/signin';
import SignUp from './pages/signup';                 // <-- Import SignUp
import ForgotPassword from './pages/forgotpasseword';

function App() {
  return (
    <Router>
      <Routes>
        {/* Routes publiques (authentification) */}
        <Route path="/signin" element={<SignIn />} />
        <Route path="/signup" element={<SignUp />} />      {/* <-- Route SignUp */}
        <Route path="/forgot-password" element={<ForgotPassword />} />

        {/* Routes privées (après connexion) */}
        <Route path="/" element={<DashboardUserLayout />}>
          <Route index element={<Home />} />
          <Route path="comment-ca-marche" element={<CommentCaMarche />} />
          <Route path="importer" element={<ImporterDonnees />} />
          <Route path="historique" element={<Historique />} />
          <Route path="mon-compte" element={<MonCompte />} />
        </Route>
      </Routes>
    </Router>
  );
}

export default App;
