import React, { useState } from 'react';
import { FiUpload, FiEdit, FiCheckCircle, FiSave, FiDatabase } from 'react-icons/fi';
import * as XLSX from 'xlsx';

function Importation() {
  const [file, setFile] = useState(null);
  const [data, setData] = useState([]);
  const [editMode, setEditMode] = useState(false);
  const [editFinished, setEditFinished] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const rowsPerPage = 20;

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
    setEditFinished(false);
    setEditMode(false);
    setCurrentPage(1);

    const reader = new FileReader();
    reader.onload = (evt) => {
      const bstr = evt.target.result;
      const wb = XLSX.read(bstr, { type: "binary" });
      const wsname = wb.SheetNames[0];
      const ws = wb.Sheets[wsname];
      const jsonData = XLSX.utils.sheet_to_json(ws, { header: 1 });
      setData(jsonData);
    };
    reader.readAsBinaryString(selectedFile);
  };

  const handleEdit = () => {
    setEditMode(true);
    setEditFinished(false);
  };

  const handleFinishEdit = () => {
    setEditMode(false);
    setEditFinished(true);
  };

  const handleCellChange = (rowIndex, colIndex, value) => {
    const newData = [...data];
    newData[rowIndex][colIndex] = value;
    setData(newData);
    setCurrentPage(1);
  };

  const handleSaveCopy = () => {
    const ws = XLSX.utils.aoa_to_sheet(data);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Feuille1");
    XLSX.writeFile(wb, "donnees_modifiees.xlsx");
  };

  const handleImportToDB = () => {
    console.log("Importer vers la base de données…");
    // Appel backend ici
  };

  const totalPages = Math.ceil(data.length / rowsPerPage);
  const startIndex = (currentPage - 1) * rowsPerPage;
  const endIndex = startIndex + rowsPerPage;
  const pageData = data.slice(startIndex, endIndex);

  // Style CSS à appliquer aux inputs en mode édition pour garder le même style que les spans
  const inputStyle = {
    width: '100%',
    border: 'none',
    backgroundColor: 'transparent',
    padding: '12px 16px', // même padding que td
    fontFamily: 'inherit',
    fontSize: 'inherit',
    lineHeight: 'inherit',
    color: 'inherit',
    outline: 'none',
    boxSizing: 'border-box',
    textAlign: 'left',
  };

  return (
    <div className="min-h-screen bg-gray-100 px-4 py-8">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-4xl font-bold text-center text-green-700 mb-8">Importation Excel</h1>

        {!file && (
          <div className="bg-white rounded-2xl shadow-lg p-10 text-center border-2 border-dashed border-green-600">
            <FiUpload className="text-green-600 mx-auto mb-4" size={50} />
            <h2 className="text-xl font-semibold text-green-700 mb-2">Déposez un fichier Excel ici</h2>
            <p className="text-gray-600 mb-6">ou cliquez pour en sélectionner un</p>
            <label className="inline-block bg-green-600 text-white px-6 py-3 rounded-lg cursor-pointer hover:bg-green-700 transition">
              Choisir un fichier Excel
              <input type="file" accept=".xlsx" className="hidden" onChange={handleFileChange} />
            </label>
          </div>
        )}

        {file && (
          <div className="mt-8">
            <div className="text-gray-700 text-center mb-4 italic">Fichier sélectionné : {file.name}</div>

            <div className="overflow-auto rounded-lg shadow-md bg-white">
              <table className="min-w-full text-sm table-auto border-collapse">
                <thead>
                  <tr className="bg-green-700 text-white">
                    {data[0] &&
                      data[0].map((col, i) => (
                        <th key={i} className="px-4 py-3 text-left font-semibold">
                          {col}
                        </th>
                      ))}
                  </tr>
                </thead>
                <tbody>
                  {pageData.slice(1).map((row, rowIndex) => (
                    <tr key={rowIndex} className="even:bg-gray-50 hover:bg-green-50">
                      {row.map((cell, colIndex) => (
                        <td key={colIndex} className="px-4 py-3 border-t border-gray-200 align-top">
                          {editMode ? (
                            <input
                              type="text"
                              value={cell || ""}
                              onChange={(e) =>
                                handleCellChange(startIndex + rowIndex + 1, colIndex, e.target.value)
                              }
                              style={inputStyle}
                              spellCheck={false}
                            />
                          ) : (
                            <span>{cell}</span>
                          )}
                        </td>
                      ))}
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            {data.length > rowsPerPage && (
              <div className="flex justify-center items-center gap-4 mt-6">
                <button
                  onClick={() => setCurrentPage((p) => Math.max(1, p - 1))}
                  disabled={currentPage === 1}
                  className="bg-gray-200 px-4 py-2 rounded hover:bg-gray-300 disabled:opacity-50"
                >
                  Précédent
                </button>
                <span>
                  Page {currentPage} / {totalPages}
                </span>
                <button
                  onClick={() => setCurrentPage((p) => Math.min(totalPages, p + 1))}
                  disabled={currentPage === totalPages}
                  className="bg-gray-200 px-4 py-2 rounded hover:bg-gray-300 disabled:opacity-50"
                >
                  Suivant
                </button>
              </div>
            )}

            <div className="flex flex-wrap justify-center gap-4 mt-6">
              {!editMode && !editFinished && (
                <>
                  <button
                    onClick={handleEdit}
                    className="bg-yellow-500 hover:bg-yellow-600 text-white px-5 py-2 rounded flex items-center gap-2"
                  >
                    <FiEdit /> Modifier
                  </button>
                  <button
                    onClick={handleImportToDB}
                    className="bg-green-700 hover:bg-green-800 text-white px-5 py-2 rounded flex items-center gap-2"
                  >
                    <FiDatabase /> Importer dans la base
                  </button>
                </>
              )}

              {editMode && (
                <button
                  onClick={handleFinishEdit}
                  className="bg-blue-600 hover:bg-blue-700 text-white px-5 py-2 rounded flex items-center gap-2"
                >
                  <FiCheckCircle /> Terminer les modifications
                </button>
              )}

              {!editMode && editFinished && (
                <>
                  <button
                    onClick={handleSaveCopy}
                    className="bg-gray-700 hover:bg-gray-800 text-white px-5 py-2 rounded flex items-center gap-2"
                  >
                    <FiSave /> Enregistrer une copie
                  </button>
                  <button
                    onClick={handleImportToDB}
                    className="bg-green-700 hover:bg-green-800 text-white px-5 py-2 rounded flex items-center gap-2"
                  >
                    <FiDatabase /> Importer dans la base
                  </button>
                </>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default Importation;
