import React, { useState, useEffect } from 'react';

function Facilite() {
  const [nomFacilite, setNomFacilite] = useState('');
  const [auteur, setAuteur] = useState('');
  const [message, setMessage] = useState('');
  const [facilites, setFacilites] = useState([]);
  const [loading, setLoading] = useState(false);

  // Charger la liste des facilit√©s via GET API
  const fetchFacilites = async () => {
    setLoading(true);
    try {
      const res = await fetch('/type_projets');
      if (!res.ok) throw new Error('Erreur lors du chargement des facilit√©s');
      const data = await res.json();
      setFacilites(data);
    } catch (err) {
      setMessage(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchFacilites();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!nomFacilite.trim() || !auteur.trim()) {
      setMessage('Merci de remplir tous les champs');
      return;
    }

    setMessage('');
    try {
      const res = await fetch('/type_projets', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ nom_facilite: nomFacilite, auteur }),
      });

      const result = await res.json();

      if (!res.ok) {
        setMessage(result.error || 'Erreur lors de l\'ajout');
      } else {
        setMessage('Facilit√© ajout√©e avec succ√®s !');
        setNomFacilite('');
        setAuteur('');
        fetchFacilites(); // Recharge la liste
      }
    } catch (err) {
      setMessage('Erreur r√©seau ou serveur');
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col items-center px-6 py-12">
      <h1 className="text-5xl font-extrabold text-green-700 mb-12 tracking-wide">Nos Facilit√©s</h1>

      <div className="w-full max-w-3xl flex flex-col md:flex-row gap-10">
        {/* Formulaire */}
        <div className="bg-white rounded-xl shadow-lg p-8 flex-1">
          <h2 className="text-3xl font-semibold text-green-700 mb-8 text-center">Ajouter une facilit√©</h2>
          <form onSubmit={handleSubmit} className="flex flex-col space-y-5">
            <input
              type="text"
              placeholder="Nom de la facilit√©"
              className="border border-gray-300 rounded-md p-4 text-lg focus:outline-none focus:ring-2 focus:ring-green-400"
              value={nomFacilite}
              onChange={(e) => setNomFacilite(e.target.value)}
            />
            <input
              type="text"
              placeholder="Auteur"
              className="border border-gray-300 rounded-md p-4 text-lg focus:outline-none focus:ring-2 focus:ring-green-400"
              value={auteur}
              onChange={(e) => setAuteur(e.target.value)}
            />
            <button
              type="submit"
              className="bg-green-600 hover:bg-green-700 transition text-white font-bold py-4 rounded-md text-lg shadow-md"
            >
              Ajouter
            </button>
            {message && (
              <p
                className={`text-center ${
                  message.includes('succ√®s') ? 'text-green-600' : 'text-red-600'
                } font-semibold mt-2`}
              >
                {message}
              </p>
            )}
          </form>
        </div>

        {/* Liste */}
        <div className="bg-white rounded-xl shadow-lg p-8 flex-1 overflow-auto max-h-[450px]">
          <h2 className="text-3xl font-semibold text-green-700 mb-8 text-center">Liste des facilit√©s</h2>
          {loading ? (
            <p className="text-center text-gray-500">Chargement en cours...</p>
          ) : facilites.length === 0 ? (
            <p className="text-center text-gray-500">Aucune facilit√© disponible pour le moment.</p>
          ) : (
            <ul className="space-y-4">
              {facilites.map((f) => (
                <li
                  key={f.id_type_projet || f.id}
                  className="border border-gray-300 rounded-md p-4 flex justify-between items-center shadow-sm hover:shadow-md transition"
                >
                  <div>
                    <p className="font-semibold text-gray-800 text-lg">{f.nom_facilite}</p>
                    <p className="text-sm text-gray-500">
                      Auteur : {f.auteur || 'N/A'} ‚Äî Cr√©√© le {f.date_creation || 'N/A'}
                    </p>
                  </div>
                  <div className="text-green-600 font-mono font-bold">{f.id_type_projet || f.id}</div>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
}

export default Facilite;





