import React, { useState } from 'react';
import { Outlet, NavLink, useNavigate, Link } from 'react-router-dom';
import {
  FiHome, FiHelpCircle, FiUpload, FiClock, FiUser, FiX, FiMenu, FiLogOut, FiSearch
} from 'react-icons/fi';
import logo from '../assets/logo.png';

function DashboardUserLayout() {
  const navigate = useNavigate();
  const [isSidebarOpen, setIsSidebarOpen] = useState(false); // fermé par défaut sur mobile
  const username = "Sergio ADOLPHE"; // à remplacer plus tard par des données dynamiques

  // Classes des liens nav, selon si sidebar ouverte
  const linkClasses = ({ isActive }) =>
    `flex items-center gap-3 px-4 py-3 rounded-lg font-medium transition-all ${
      isActive
        ? 'bg-green-600 text-white shadow'
        : 'text-gray-700 hover:bg-gray-200'
    }`;

  const handleLogout = () => {
    console.log('Déconnexion...');
    navigate('/');
  };

  return (
    <div className="flex h-screen overflow-hidden bg-white">

      {/* Sidebar - responsive */}
      <aside
        className={`
          fixed inset-y-0 left-0 z-30
          w-64 bg-white p-4 border-r border-gray-100
          transform transition-transform duration-300 ease-in-out
          md:relative md:translate-x-0
          ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'}
          md:translate-x-0
          flex flex-col justify-between
        `}
      >
        {/* Toggle pour mobile */}
        <div className="flex justify-end mb-4 md:hidden">
          <button
            onClick={() => setIsSidebarOpen(false)}
            className="text-gray-600 hover:text-gray-900"
            aria-label="Fermer le menu"
          >
            <FiX size={24} />
          </button>
        </div>

        {/* Logo */}
        <div className="flex justify-center mb-10">
          <img
            src={logo}
            alt="Logo"
            className="h-24 select-none"
          />
        </div>

        {/* Navigation */}
        <nav className="space-y-4 flex-grow">
          <NavLink to="/dashboard" end className={linkClasses}>
            <FiHome /> Accueil
          </NavLink>
          <NavLink to="/dashboard/comment-ca-marche" className={linkClasses}>
            <FiHelpCircle /> Fonctionnement
          </NavLink>
          <NavLink to="/dashboard/facilite" className={linkClasses}>
            <FiTool /> Facilité
          </NavLink>
          <NavLink to="/dashboard/importer" className={linkClasses}>
            <FiUpload /> Importer des données
          </NavLink>

          <NavLink to="/dashboard/historique" className={linkClasses}>
            <FiClock /> Historique
          </NavLink>
        </nav>

        {/* Mon Compte en bas */}
        <NavLink to="/dashboard/mon-compte" className={linkClasses}>
          <FiUser /> Mon compte
        </NavLink>
      </aside>

      {/* Overlay sombre derrière sidebar quand ouvert sur mobile */}
      {isSidebarOpen && (
        <div
          onClick={() => setIsSidebarOpen(false)}
          className="fixed inset-0 bg-black bg-opacity-30 z-20 md:hidden"
          aria-hidden="true"
        />
      )}

      {/* Zone principale */}
      <div className="flex flex-col flex-1 overflow-hidden">

        {/* Topbar */}
        <header className="w-full h-16 bg-white flex items-center justify-between px-4 md:px-6 border-b border-gray-200">

          {/* Bouton menu mobile */}
          <button
            onClick={() => setIsSidebarOpen(true)}
            className="text-gray-600 hover:text-gray-900 md:hidden"
            aria-label="Ouvrir le menu"
          >
            <FiMenu size={24} />
          </button>

          {/* Barre de recherche - prend toute la place dispo */}
          <div className="relative flex-1 max-w-md mx-4 md:mx-0">
            <span className="absolute left-3 top-2.5 text-gray-400 pointer-events-none">
              <FiSearch />
            </span>
            <input
              type="text"
              placeholder="Rechercher..."
              className="w-full pl-10 pr-4 py-2 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-green-500"
            />
          </div>

          {/* Profil + déconnexion */}
          <div className="flex items-center space-x-4 ml-4">
            <span className="text-gray-700 font-medium hidden sm:inline select-none">
              Bonjour, <span className="text-green-600">{username}</span>
            </span>
            <button
              onClick={handleLogout}
              className="flex items-center gap-2 bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
              aria-label="Se déconnecter"
            >
              <FiLogOut />
              <Link to="/" className=" hover:underline hidden sm:inline">
                Se déconnecter
              </Link>
              {/* <a href="/signin" className=" hover:underline hidden sm:inline">
              Se déconnecter
            </a> */}
              {/* <span className=""></span> */}
            </button>
          </div>

        </header>

        {/* Contenu principal */}
        <main className="flex-1 overflow-auto p-6 md:p-10 bg-gray-50">
          <Outlet />
        </main>
      </div>
    </div>
  );
}

export default DashboardUserLayout;
