import React from 'react';
import { FiUpload, FiEdit3, FiDatabase, FiCheckCircle } from 'react-icons/fi';

function CommentCaMarche() {
  const steps = [
    {
      icon: <FiUpload size={32} />,
      title: '1. Importer un fichier Excel',
      description: 'Téléversez facilement votre fichier Excel contenant les données à traiter.',
    },
    {
      icon: <FiEdit3 size={32} />,
      title: '2. Visualiser et modifier',
      description: 'Consultez vos données, effectuez des ajustements ou corrigez des erreurs si nécessaire.',
    },
    {
      icon: <FiDatabase size={32} />,
      title: '3. Enregistrer dans la base',
      description: 'Une fois validées, les données sont insérées dans la base PostgreSQL.',
    },
    {
      icon: <FiCheckCircle size={32} />,
      title: '4. Suivre votre historique',
      description: 'Consultez les fichiers précédemment importés et les modifications apportées.',
    },
  ];

  return (
    <div className="min-h-screen flex flex-col justify-center items-center bg-white px-4 py-10">
      <h1 className="text-4xl font-bold text-green-700 mb-10 animate-slide-in-left">Comment ça marche ?</h1>
      
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8 w-full max-w-5xl">
        {steps.map((step, index) => (
          <div
            key={index}
            className="bg-white rounded-2xl shadow-md p-6 border border-gray-100 hover:shadow-lg transition duration-300"
          >
            <div className="flex items-center mb-4 text-green-600">{step.icon}</div>
            <h3 className="text-xl font-semibold text-gray-800 mb-2">{step.title}</h3>
            <p className="text-gray-600">{step.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CommentCaMarche;
