import React, { useState } from 'react';
import { FiUpload, FiEdit, FiCheckCircle, FiDatabase } from 'react-icons/fi';
import * as XLSX from 'xlsx';

function Importation() {
  const [file, setFile] = useState(null);
  const [data, setData] = useState([]);
  const [editMode, setEditMode] = useState(false);
  const [editFinished, setEditFinished] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [importStatus, setImportStatus] = useState(null);
  const [facilite, setFacilite] = useState('');
  const [faciliteValidee, setFaciliteValidee] = useState(false);

  const rowsPerPage = 101;

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
    setEditFinished(false);
    setEditMode(false);
    setCurrentPage(1);
    setImportStatus(null);

    const reader = new FileReader();
    reader.onload = (evt) => {
      const bstr = evt.target.result;
      const wb = XLSX.read(bstr, { type: 'binary' });
      const wsname = wb.SheetNames[0];
      const ws = wb.Sheets[wsname];
      const jsonData = XLSX.utils.sheet_to_json(ws, { header: 1 });
      setData(jsonData);
    };
    reader.readAsBinaryString(selectedFile);
  };

  const handleEdit = () => {
    setEditMode(true);
    setEditFinished(false);
  };

  const handleFinishEdit = () => {
    setEditMode(false);
    setEditFinished(true);
  };

  const handleCellChange = (rowIndex, colIndex, value) => {
    const newData = [...data];
    newData[rowIndex][colIndex] = value;
    setData(newData);
  };

  const handleImportToDB = async () => {
    if (!file) {
      setImportStatus({ success: false, message: 'Veuillez sélectionner un fichier avant d\'importer.' });
      return;
    }

    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await fetch('http://localhost:5000/auth/import_excel', {
        credentials: "include",
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Erreur serveur : ${errorText}`);
      }

      const result = await response.json();
      setImportStatus({ success: true, message: result.message || 'Importation réussie !' });
    } catch (error) {
      setImportStatus({ success: false, message: error.message || 'Erreur lors de l\'importation.' });
    }
  };

  const totalPages = Math.ceil(data.length / rowsPerPage);
  const startIndex = (currentPage - 1) * rowsPerPage;
  const endIndex = startIndex + rowsPerPage;
  const pageData = data.slice(startIndex, endIndex);

  const inputStyle = {
    width: '100%',
    minWidth: '200px',
    border: 'none',
    backgroundColor: 'transparent',
    padding: '12px 16px',
    fontFamily: 'inherit',
    fontSize: 'inherit',
    lineHeight: 'inherit',
    color: 'black',
    outline: 'none',
    boxSizing: 'border-box',
    textAlign: 'left',
  };

  return (
    <div className="min-h-screen bg-gray-100 px-4 py-8">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-4xl md:text-5xl font-extrabold text-center text-green-700 mb-8">Importation Excel</h1>

        {/* Module 1 : Choix du type de facilité */}
        <div className="bg-white rounded-xl shadow-md p-6 mb-8 max-w-md mx-auto border border-green-600">
          <h2 className="text-xl font-semibold text-green-700 mb-4 text-center">Choisir le type de facilité</h2>
          <select
            value={facilite}
            onChange={(e) => setFacilite(e.target.value)}
            className="w-full p-3 border border-gray-300 rounded mb-4"
          >
            <option value="">-- Sélectionnez une facilité --</option>
            <option value="FACILITE_A">Facilité A</option>
            <option value="FACILITE_B">Facilité B</option>
            <option value="FACILITE_C">Facilité C</option>
          </select>
          <button
            onClick={() => {
              if (facilite) setFaciliteValidee(true);
              else alert("Veuillez sélectionner un type de facilité.");
            }}
            className="bg-green-600 hover:bg-green-700 text-white px-6 py-2 rounded w-full"
          >
            Valider
          </button>
        </div>

        {/* Module 2 : Import Excel (masqué si aucune facilité sélectionnée) */}
        {faciliteValidee && (
          <>
            {!file && (
              <div className="bg-white rounded-xl shadow-lg p-10 text-center border-2 border-dashed border-green-600 w-96 mx-auto">
                <FiUpload className="text-green-600 mx-auto mb-4" size={80} />
                <h2 className="text-xl font-semibold text-green-700 mb-2">Déposez un fichier Excel ici</h2>
                <p className="text-gray-600 mb-6">Format accepté : .xlsx</p>
                <label className="inline-block bg-green-600 text-white px-6 py-3 rounded-lg cursor-pointer hover:bg-green-700 transition">
                  Choisir un fichier Excel
                  <input type="file" accept=".xlsx" className="hidden" onChange={handleFileChange} />
                </label>
              </div>
            )}

            {file && (
              <div className="mt-8">
                <div className="text-gray-700 text-center mb-4 italic">Fichier sélectionné : {file.name}</div>

                <div className="overflow-auto rounded-lg shadow-md bg-white">
                  <table className="min-w-full text-sm table-auto border-collapse">
                    <thead>
                      <tr className="bg-green-700 text-white">
                        {data[0] &&
                          data[0].map((col, i) => (
                            <th key={i} className="px-4 py-3 text-left font-semibold">{col}</th>
                          ))}
                      </tr>
                    </thead>
                    <tbody>
                      {pageData.slice(1).map((row, rowIndex) => (
                        <tr key={rowIndex} className="even:bg-gray-50 hover:bg-green-50">
                          {row.map((cell, colIndex) => (
                            <td key={colIndex} className="px-4 py-3 border-t border-gray-200 align-top" style={editMode ? { minWidth: '200px' } : {}}>
                              {editMode ? (
                                <input
                                  type="text"
                                  value={cell || ''}
                                  onChange={(e) =>
                                    handleCellChange(startIndex + rowIndex + 1, colIndex, e.target.value)
                                  }
                                  style={inputStyle}
                                  spellCheck={false}
                                />
                              ) : (
                                <span>{cell}</span>
                              )}
                            </td>
                          ))}
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>

                {data.length > rowsPerPage && (
                  <div className="flex justify-center items-center gap-4 mt-6">
                    <button
                      onClick={() => setCurrentPage((p) => Math.max(1, p - 1))}
                      disabled={currentPage === 1}
                      className="bg-gray-200 px-4 py-2 rounded hover:bg-gray-300 disabled:opacity-50"
                    >
                      Précédent
                    </button>
                    <span>Page {currentPage} / {totalPages}</span>
                    <button
                      onClick={() => setCurrentPage((p) => Math.min(totalPages, p + 1))}
                      disabled={currentPage === totalPages}
                      className="bg-gray-200 px-4 py-2 rounded hover:bg-gray-300 disabled:opacity-50"
                    >
                      Suivant
                    </button>
                  </div>
                )}

                <div className="flex flex-wrap justify-center gap-4 mt-6">
                  {!editMode && !editFinished && (
                    <>
                      <button
                        onClick={handleEdit}
                        className="bg-yellow-500 hover:bg-yellow-600 text-white px-5 py-2 rounded flex items-center gap-2"
                      >
                        <FiEdit /> Modifier
                      </button>
                      <button
                        onClick={handleImportToDB}
                        className="bg-green-700 hover:bg-green-800 text-white px-5 py-2 rounded flex items-center gap-2"
                      >
                        <FiDatabase /> Importer dans la base
                      </button>
                    </>
                  )}

                  {editMode && (
                    <button
                      onClick={handleFinishEdit}
                      className="bg-blue-600 hover:bg-blue-700 text-white px-5 py-2 rounded flex items-center gap-2"
                    >
                      <FiCheckCircle /> Terminer les modifications
                    </button>
                  )}
                </div>

                {importStatus && (
                  <div className={`mt-4 text-center font-semibold ${importStatus.success ? 'text-green-600' : 'text-red-600'}`}>
                    {importStatus.message}
                  </div>
                )}
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
}

export default Importation;
