import React, { useState } from 'react';
import { FiUploadCloud, FiFileText, FiArrowRightCircle } from 'react-icons/fi';

function Importation() {
  const [file, setFile] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleImport = () => {
    if (file) {
      console.log('Fichier importé :', file.name);
      // Tu peux ici appeler ton backend Flask
    } else {
      alert("Veuillez sélectionner un fichier Excel.");
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-white py-10 px-4">
      <div className="text-center mb-10">
        <h1 className="text-3xl md:text-4xl font-bold text-green-700 mb-4 animate-slide-in-left">
          Importer vos données Excel
        </h1>
        <p className="text-gray-600 max-w-xl mx-auto">
          Glissez-déposez un fichier .xlsx ou sélectionnez-en un depuis votre ordinateur. Assurez-vous que le fichier suit la structure attendue.
        </p>
      </div>

      <div className="w-full max-w-2xl bg-gray-50 p-8 rounded-2xl shadow-md border border-dashed border-green-400 hover:shadow-lg transition">
        <div className="flex flex-col items-center space-y-4">
          <FiUploadCloud size={48} className="text-green-600" />
          <label className="cursor-pointer bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded-lg font-medium transition">
            Choisir un fichier Excel
            <input type="file" accept=".xlsx" className="hidden" onChange={handleFileChange} />
          </label>
          {file && (
            <div className="flex items-center gap-2 mt-4 text-gray-700">
              <FiFileText />
              <span>{file.name}</span>
            </div>
          )}
          <button
            onClick={handleImport}
            className="mt-6 flex items-center gap-2 bg-green-700 hover:bg-green-800 text-white px-6 py-2 rounded-lg font-semibold transition"
          >
            Importer <FiArrowRightCircle />
          </button>
        </div>
      </div>
    </div>
  );
}

export default Importation;
