// importeesdonnees.js
// Ce composant React permet d'importer un fichier Excel, de le visualiser, de le modifier et de l'importer en base PostgreSQL avec pagination.

import React, { useState } from 'react';
import { FiUpload, FiFileText, FiEdit, FiCheckCircle, FiSave, FiDatabase } from 'react-icons/fi';
import * as XLSX from 'xlsx';

function ImporteesDonnees() {
  const [file, setFile] = useState(null);
  const [data, setData] = useState([]);
  const [editMode, setEditMode] = useState(false);
  const [editFinished, setEditFinished] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const rowsPerPage = 100;

  // Lecture et conversion du fichier Excel en tableau JSON
  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);

    const reader = new FileReader();
    reader.onload = (evt) => {
      const bstr = evt.target.result;
      const wb = XLSX.read(bstr, { type: "binary" });
      const wsname = wb.SheetNames[0];
      const ws = wb.Sheets[wsname];
      const jsonData = XLSX.utils.sheet_to_json(ws, { header: 1 });
      setData(jsonData);
      setEditMode(false);
      setEditFinished(false);
      setCurrentPage(1);
    };
    reader.readAsBinaryString(selectedFile);
  };

  // Entrer en mode édition
  const handleEdit = () => {
    setEditMode(true);
  };

  // Quitter le mode édition
  const handleFinishEdit = () => {
    setEditMode(false);
    setEditFinished(true);
  };

  // Modifier la valeur d'une cellule
  const handleCellChange = (rowIndex, colIndex, value) => {
    const newData = [...data];
    newData[rowIndex][colIndex] = value;
    setData(newData);
  };

  // Enregistrer une copie Excel modifiée
  const handleSaveCopy = () => {
    const ws = XLSX.utils.aoa_to_sheet(data);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Feuille1");
    XLSX.writeFile(wb, "donnees_modifiees.xlsx");
  };

  // Simulation d'envoi au backend
  const handleImportToDB = () => {
    console.log("Importation vers la base de données...");
    // Appeler ici une API Flask avec fetch ou axios
  };

  // Pagination
  const totalPages = Math.ceil(data.length / rowsPerPage);
  const currentData = data.slice(
    (currentPage - 1) * rowsPerPage,
    currentPage * rowsPerPage
  );

  const handlePrevPage = () => {
    if (currentPage > 1) setCurrentPage(currentPage - 1);
  };

  const handleNextPage = () => {
    if (currentPage < totalPages) setCurrentPage(currentPage + 1);
  };

  return (
    <div className="min-h-screen bg-white p-6 flex flex-col items-center">
      <h1 className="text-3xl font-bold text-green-700 mb-6">Importation de fichier Excel</h1>

      {/* Zone de sélection du fichier avant importation - Design conservé */}
      {!file && (
        <div className="w-full max-w-xl p-8 bg-white border-2 text-xl rounded-2xl shadow-md text-center transition hover:shadow-lg hover:bg-green-50 mb-10 mt-14">
          <FiUpload className="mx-auto text-green-600" size={50} />
          <h2 className="text-xl font-semibold text-green-700 mt-4 mb-2">Déposez votre fichier Excel ici</h2>
          <p className="text-gray-600 mb-4">ou cliquez pour en sélectionner un sur votre ordinateur</p>

          <label className="cursor-pointer inline-block bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded-lg font-medium transition">
            Prendre un fichier Excel
            <input type="file" accept=".xlsx" className="hidden" onChange={handleFileChange} />
          </label>

          <p className="text-sm text-gray-500 mt-4 italic">
            Format accepté : <strong>.xlsx</strong> – veillez à respecter la structure demandée
          </p>
        </div>
      )}

      {/* Affichage du tableau Excel après importation */}
      {file && (
        <div className="w-full mt-6 flex flex-col items-center">
          <div className="mb-4 text-center text-gray-600">
            <FiFileText className="inline-block mr-2" /> {file.name}
          </div>

          <div className="overflow-auto max-w-full mb-4 border border-gray-300 rounded-md">
            <table className="table-auto w-full text-sm text-left border-collapse">
              <tbody>
                {currentData.map((row, rowIndex) => (
                  <tr key={rowIndex} className="even:bg-gray-50">
                    {row.map((cell, colIndex) => (
                      <td key={colIndex} className="border px-3 py-2">
                        {editMode ? (
                          <input
                            value={cell}
                            onChange={(e) => handleCellChange((currentPage - 1) * rowsPerPage + rowIndex, colIndex, e.target.value)}
                            className="w-full bg-transparent focus:outline-none border-b border-gray-400"
                          />
                        ) : (
                          <span>{cell}</span>
                        )}
                      </td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* Pagination */}
          {data.length > rowsPerPage && (
            <div className="flex gap-4 mb-4">
              <button
                onClick={handlePrevPage}
                disabled={currentPage === 1}
                className="px-4 py-1 bg-gray-300 hover:bg-gray-400 rounded disabled:opacity-50"
              >
                Précédent
              </button>
              <span className="text-sm text-gray-600">Page {currentPage} / {totalPages}</span>
              <button
                onClick={handleNextPage}
                disabled={currentPage === totalPages}
                className="px-4 py-1 bg-gray-300 hover:bg-gray-400 rounded disabled:opacity-50"
              >
                Suivant
              </button>
            </div>
          )}

          {/* Actions selon le mode */}
          {!editMode && !editFinished && (
            <div className="flex gap-4">
              <button
                onClick={handleEdit}
                className="bg-yellow-500 hover:bg-yellow-600 text-white px-4 py-2 rounded flex items-center gap-2"
              >
                <FiEdit /> Modifier
              </button>
              <button
                onClick={handleImportToDB}
                className="bg-green-700 hover:bg-green-800 text-white px-4 py-2 rounded flex items-center gap-2"
              >
                <FiDatabase /> Importer dans la base
              </button>
            </div>
          )}

          {editMode && (
            <button
              onClick={handleFinishEdit}
              className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded flex items-center gap-2"
            >
              <FiCheckCircle /> Terminer
            </button>
          )}

          {!editMode && editFinished && (
            <div className="flex gap-4 mt-4">
              <button
                onClick={handleSaveCopy}
                className="bg-gray-700 hover:bg-gray-800 text-white px-4 py-2 rounded flex items-center gap-2"
              >
                <FiSave /> Enregistrer une copie
              </button>
              <button
                onClick={handleImportToDB}
                className="bg-green-700 hover:bg-green-800 text-white px-4 py-2 rounded flex items-center gap-2"
              >
                <FiDatabase /> Importer dans la base
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default ImporteesDonnees;
