import React from 'react';
import { Outlet, NavLink } from 'react-router-dom';

function DashboardUserLayout() {
  const linkClasses = ({ isActive }) =>
    `block px-4 py-2 rounded-lg text-sm font-medium ${
      isActive ? 'bg-blue-600 text-white' : 'text-gray-700 hover:bg-gray-100'
    }`;

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Sidebar */}
      <aside className="w-64 bg-white shadow-md p-4">
        <h1 className="text-xl font-bold mb-6 text-blue-700">Dashboard Utilisateur</h1>
        <nav className="space-y-2">
          <NavLink to="/" end className={linkClasses}>
            Comment ça marche
          </NavLink>
          <NavLink to="/importer" className={linkClasses}>
            Importer des données
          </NavLink>
          <NavLink to="/historique" className={linkClasses}>
            Historique
          </NavLink>
          <NavLink to="/mon-compte" className={linkClasses}>
            Mon compte
          </NavLink>
        </nav>
      </aside>

      {/* Contenu principal */}
      <main className="flex-1 p-6">
        <Outlet />
      </main>
    </div>
  );
}

export default DashboardUserLayout;
