import React, { useState, useRef, useEffect } from 'react';
import {
  FiUser, FiMail, FiLock, FiEdit3, FiCheck, FiX,
  FiEye, FiEyeOff, FiCamera, FiImage,
} from 'react-icons/fi';

function MonCompte() {
  const [user, setUser] = useState(null); // Objet utilisateur complet
  const [editField, setEditField] = useState(null);
  const [tempValues, setTempValues] = useState({});
  const [passwordFields, setPasswordFields] = useState({
    current: '',
    new: '',
    confirm: '',
  });
  const [showPassword, setShowPassword] = useState({
    current: false,
    new: false,
    confirm: false,
  });

  // Avatar
  const [avatar, setAvatar] = useState(null);
  const [isCameraOpen, setIsCameraOpen] = useState(false);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  // Charger l'utilisateur depuis le localStorage au montage
  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      try {
        const parsedUser = JSON.parse(storedUser);
        setUser(parsedUser);
        setTempValues({
          nom: parsedUser.nom || '',
          prenom: parsedUser.prenom || '',
          email: parsedUser.email || '',
        });
      } catch (error) {
        console.error('Erreur de parsing JSON :', error);
      }
    }
  }, []);

  const handleEditClick = (field) => {
    setEditField(field);
    if (field !== 'password') {
      setTempValues(prev => ({
        ...prev,
        [field]: user?.[field] || ''
      }));
    } else {
      setPasswordFields({ current: '', new: '', confirm: '' });
    }
  };

  const handleCancel = () => {
    setEditField(null);
  };

  const handleSave = () => {
    if (!user) return;
    let updatedUser = { ...user };

    if (editField === 'nom' || editField === 'prenom' || editField === 'email') {
      updatedUser[editField] = tempValues[editField];
      setUser(updatedUser);
      localStorage.setItem('user', JSON.stringify(updatedUser));
    }

    if (editField === 'password') {
      if (
        passwordFields.new.length >= 6 &&
        passwordFields.new === passwordFields.confirm
      ) {
        alert("Mot de passe mis à jour avec succès !");
      } else {
        alert("Erreur : vérifiez les champs du mot de passe.");
        return;
      }
    }

    setEditField(null);
  };

  const toggleShowPassword = (field) => {
    setShowPassword(prev => ({ ...prev, [field]: !prev[field] }));
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    if (!file.type.startsWith("image/")) {
      alert("Veuillez choisir une image.");
      return;
    }
    const reader = new FileReader();
    reader.onload = () => setAvatar(reader.result);
    reader.readAsDataURL(file);
  };

  const openCamera = async () => {
    setIsCameraOpen(true);
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        videoRef.current.play();
      }
    } catch (err) {
      alert("Erreur d'accès à la caméra : " + err.message);
      setIsCameraOpen(false);
    }
  };

  const capturePhoto = () => {
    if (!videoRef.current || !canvasRef.current) return;
    const video = videoRef.current;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
    const imageDataUrl = canvas.toDataURL('image/png');
    setAvatar(imageDataUrl);
    const stream = video.srcObject;
    if (stream) stream.getTracks().forEach(track => track.stop());
    setIsCameraOpen(false);
  };

  const cancelCamera = () => {
    if (videoRef.current?.srcObject) {
      videoRef.current.srcObject.getTracks().forEach(track => track.stop());
    }
    setIsCameraOpen(false);
  };

  const renderPasswordInput = (fieldKey, placeholder, value, onChange) => (
    <div className="relative">
      <input
        type={showPassword[fieldKey] ? "text" : "password"}
        placeholder={placeholder}
        value={value}
        onChange={onChange}
        className="input-style pr-10"
      />
      <button
        type="button"
        onClick={() => toggleShowPassword(fieldKey)}
        className="absolute right-2 top-1/2 transform -translate-y-1/2 text-gray-500 hover:text-gray-700"
      >
        {showPassword[fieldKey] ? <FiEyeOff size={20} /> : <FiEye size={20} />}
      </button>
    </div>
  );

  const renderField = (label, key, icon) => {
    const isEditing = editField === key;
    const value = user?.[key] || '';

    return (
      <div className="flex flex-col gap-2 bg-white p-5 rounded-xl shadow border">
        <div className="flex items-center gap-3 mb-1">
          {icon}
          <h4 className="text-gray-600 font-medium">{label}</h4>
        </div>

        {!isEditing ? (
          <div className="flex justify-between items-center">
            <p className="text-lg font-semibold text-gray-800">
              {key === 'password' ? '********' : value}
            </p>
            <button
              onClick={() => handleEditClick(key)}
              className="text-green-600 hover:text-green-800"
            >
              <FiEdit3 size={20} />
            </button>
          </div>
        ) : (
          <>
            {key === "password" ? (
              <>
                {renderPasswordInput('current', "Mot de passe actuel", passwordFields.current, e => setPasswordFields({ ...passwordFields, current: e.target.value }))}
                {renderPasswordInput('new', "Nouveau mot de passe", passwordFields.new, e => setPasswordFields({ ...passwordFields, new: e.target.value }))}
                {renderPasswordInput('confirm', "Confirmer le mot de passe", passwordFields.confirm, e => setPasswordFields({ ...passwordFields, confirm: e.target.value }))}
              </>
            ) : (
              <input
                type="text"
                value={tempValues[key]}
                onChange={e => setTempValues({ ...tempValues, [key]: e.target.value })}
                className="input-style"
              />
            )}
            <div className="flex gap-2 mt-2">
              <button onClick={handleSave} className="text-green-600 hover:text-green-800">
                <FiCheck size={20} />
              </button>
              <button onClick={handleCancel} className="text-red-500 hover:text-red-700">
                <FiX size={20} />
              </button>
            </div>
          </>
        )}
      </div>
    );
  };

  return (
    <div className="w-full min-h-screen bg-gray-50 flex flex-col items-center px-4 py-10">
      <h2 className="text-4xl font-bold text-green-700 mb-8">Mon compte</h2>

      <div className="w-full max-w-4xl bg-white rounded-3xl shadow-xl p-6 md:p-10 flex flex-col md:flex-row gap-10 items-center md:items-start">
        {/* Avatar */}
        <div className="flex flex-col items-center gap-4">
          <div
            onClick={openCamera}
            className="cursor-pointer w-32 h-32 md:w-40 md:h-40 rounded-full overflow-hidden border-4 border-green-100 shadow-md flex items-center justify-center bg-gray-100"
          >
            {avatar ? (
              <img src={avatar} alt="avatar" className="object-cover w-full h-full" />
            ) : (
              <FiUser className="text-green-600 w-20 h-20 md:w-24 md:h-24 p-6" />
            )}
          </div>

          <label
            htmlFor="fileInput"
            className="cursor-pointer flex items-center gap-2 text-green-600 hover:text-green-800 font-medium"
          >
            <FiImage size={24} /> Choisir une image depuis la galerie
          </label>
          <input
            type="file"
            id="fileInput"
            accept="image/*"
            onChange={handleFileChange}
            className="hidden"
          />

          {isCameraOpen && (
            <div className="fixed inset-0 bg-black bg-opacity-70 flex flex-col items-center justify-center z-50 p-4">
              <video ref={videoRef} className="rounded-lg max-w-full max-h-[70vh]" />
              <div className="mt-4 flex gap-4">
                <button
                  onClick={capturePhoto}
                  className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
                >
                  Prendre la photo
                </button>
                <button
                  onClick={cancelCamera}
                  className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
                >
                  Annuler
                </button>
              </div>
              <canvas ref={canvasRef} className="hidden" />
            </div>
          )}
        </div>

        {/* Formulaire */}
        <div className="flex flex-col flex-grow gap-6 w-full max-w-md">
          {renderField("Nom", "nom", <FiUser size={24} className="text-green-600" />)}
          {renderField("Prénom", "prenom", <FiUser size={24} className="text-green-600" />)}
          {renderField("Adresse email", "email", <FiMail size={24} className="text-green-600" />)}
          {renderField("Mot de passe", "password", <FiLock size={24} className="text-green-600" />)}
        </div>
      </div>
    </div>
  );
}

export default MonCompte;
