import React from 'react';
import { FiUser, FiMail, FiLock, FiEdit3 } from 'react-icons/fi';

function MonCompte() {
  const username = "Utilisateur";
  const email = "utilisateur@example.com";

  const handleEdit = (field) => {
    alert(`Modification du champ : ${field}`);
    // Tu pourras remplacer ceci par un vrai formulaire modal ou inline
  };

  return (
    <div className="w-full h-screen bg-white flex flex-col items-center justify-start p-6 overflow-auto">
      {/* Titre principal */}
      <h2 className="text-3xl md:text-4xl font-bold text-green-700 mb-8 animate-slide-in-left">
        Mon compte
      </h2>

      {/* Bloc détaché */}
      <div className="w-full max-w-xl p-6 bg-gray-50 rounded-2xl shadow-lg space-y-6">
        {/* Bloc Nom */}
        <div className="flex items-center justify-between p-4 bg-white rounded-lg shadow">
          <div className="flex items-center gap-4">
            <FiUser className="text-green-600" size={24} />
            <div>
              <p className="text-sm text-gray-500">Nom d’utilisateur</p>
              <p className="text-lg font-semibold">{username}</p>
            </div>
          </div>
          <button
            onClick={() => handleEdit('Nom d’utilisateur')}
            className="text-green-600 hover:text-green-800 transition"
          >
            <FiEdit3 size={20} />
          </button>
        </div>

        {/* Bloc Email */}
        <div className="flex items-center justify-between p-4 bg-white rounded-lg shadow">
          <div className="flex items-center gap-4">
            <FiMail className="text-green-600" size={24} />
            <div>
              <p className="text-sm text-gray-500">Adresse email</p>
              <p className="text-lg font-semibold">{email}</p>
            </div>
          </div>
          <button
            onClick={() => handleEdit('Adresse email')}
            className="text-green-600 hover:text-green-800 transition"
          >
            <FiEdit3 size={20} />
          </button>
        </div>

        {/* Bloc Mot de passe */}
        <div className="flex items-center justify-between p-4 bg-white rounded-lg shadow">
          <div className="flex items-center gap-4">
            <FiLock className="text-green-600" size={24} />
            <div>
              <p className="text-sm text-gray-500">Mot de passe</p>
              <p className="text-lg font-semibold">********</p>
            </div>
          </div>
          <button
            onClick={() => handleEdit('Mot de passe')}
            className="text-green-600 hover:text-green-800 transition"
          >
            <FiEdit3 size={20} />
          </button>
        </div>
      </div>
    </div>
  );
}

export default MonCompte;
