import React from 'react';
import { FiUpload, FiEye, FiEdit2, FiCheckCircle } from 'react-icons/fi';

function CommentCaMarche() {
  return (
    <div className="w-full h-screen bg-white flex flex-col items-center justify-start p-6 overflow-auto">
      {/* Titre animé */}
      <h2 className="text-3xl md:text-4xl font-bold text-green-700 mb-10 animate-slide-in-left">
        Comment ça marche ?
      </h2>

      {/* Étapes */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8 w-full max-w-6xl">
        {/* Étape 1 */}
        <div className="flex flex-col items-center text-center p-6 rounded-lg shadow hover:shadow-lg transition">
          <FiUpload size={40} className="text-green-600 mb-4" />
          <h3 className="text-xl font-semibold mb-2">1. Importez</h3>
          <p className="text-gray-600">Choisissez votre fichier Excel et importez-le en quelques clics.</p>
        </div>

        {/* Étape 2 */}
        <div className="flex flex-col items-center text-center p-6 rounded-lg shadow hover:shadow-lg transition">
          <FiEye size={40} className="text-green-600 mb-4" />
          <h3 className="text-xl font-semibold mb-2">2. Visualisez</h3>
          <p className="text-gray-600">Consultez un aperçu de vos données directement sur la plateforme.</p>
        </div>

        {/* Étape 3 */}
        <div className="flex flex-col items-center text-center p-6 rounded-lg shadow hover:shadow-lg transition">
          <FiEdit2 size={40} className="text-green-600 mb-4" />
          <h3 className="text-xl font-semibold mb-2">3. Modifiez</h3>
          <p className="text-gray-600">Corrigez ou ajustez les lignes avant de les valider définitivement.</p>
        </div>

        {/* Étape 4 */}
        <div className="flex flex-col items-center text-center p-6 rounded-lg shadow hover:shadow-lg transition">
          <FiCheckCircle size={40} className="text-green-600 mb-4" />
          <h3 className="text-xl font-semibold mb-2">4. Importation finale</h3>
          <p className="text-gray-600">Importez vos données dans la base PostgreSQL en un clic.</p>
        </div>
      </div>
    </div>
  );
}

export default CommentCaMarche;
