import React, { useState, useEffect } from 'react';

function Facilite() {
  const [nomFacilite, setNomFacilite] = useState('');
  const [auteur, setAuteur] = useState('');
  const [message, setMessage] = useState('');
  const [facilites, setFacilites] = useState([]);
  const [loading, setLoading] = useState(false);

  // RÃ©cupÃ©rer la liste des facilitÃ©s au chargement
  useEffect(() => {
    const fetchFacilites = async () => {
      setLoading(true);
      try {
        const res = await fetch('http://localhost:5000/auth/type_projets');
        if (!res.ok) throw new Error('Erreur lors du chargement des facilitÃ©s');
        const data = await res.json();
        setFacilites(data);
      } catch (error) {
        setMessage(error.message);
      } finally {
        setLoading(false);
      }
    };
    fetchFacilites();
  }, []);

  // Gestion de l'ajout
  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');

    if (!nomFacilite.trim() || !auteur.trim()) {
      setMessage('Merci de remplir tous les champs');
      return;
    }

    try {
      const res = await fetch('http://localhost:5000/auth/type_projets', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ nom_facilite: nomFacilite, auteur }),
      });

      const data = await res.json();

      if (!res.ok) {
        // Affiche message d'erreur retournÃ© par le backend
        setMessage(data.error || 'Erreur lors de l\'ajout de la facilitÃ©');
        return;
      }

      // Ajout rÃ©ussi : on ajoute la nouvelle facilitÃ© Ã  la liste locale
      setFacilites([...facilites, data]);
      setNomFacilite('');
      setAuteur('');
      setMessage('FacilitÃ© ajoutÃ©e avec succÃ¨s !');
    } catch (error) {
      setMessage('Erreur rÃ©seau : impossible de joindre le serveur');
    }
  };

  return (
    <div className="w-full min-h-screen bg-gray-50 flex flex-col items-center px-4 py-10">
      <h1 className="text-5xl font-extrabold text-green-700 mb-12">Nos FacilitÃ©s</h1>

      <div className="w-full max-w-2xl bg-white rounded-lg shadow-md p-8 mb-12">
        <h2 className="text-3xl font-semibold text-green-700 mb-6 text-center">Ajouter une facilitÃ©</h2>

        <form onSubmit={handleSubmit} className="flex flex-col space-y-6">
          <input
            type="text"
            placeholder="Nom de la facilitÃ©"
            className="border border-gray-300 rounded-md p-3 focus:outline-none focus:ring-2 focus:ring-green-400"
            value={nomFacilite}
            onChange={(e) => setNomFacilite(e.target.value)}
          />
          <input
            type="text"
            placeholder="Auteur"
            className="border border-gray-300 rounded-md p-3 focus:outline-none focus:ring-2 focus:ring-green-400"
            value={auteur}
            onChange={(e) => setAuteur(e.target.value)}
          />
          <button
            type="submit"
            className="bg-green-700 hover:bg-green-600 transition text-white font-semibold py-3 rounded-md"
          >
            Ajouter
          </button>
          {message && (
            <p className={`text-center ${message.includes('succÃ¨s') ? 'text-green-600' : 'text-red-600'}`}>
              {message}
            </p>
          )}
        </form>
      </div>

      <div className="w-full max-w-2xl bg-white rounded-lg shadow-md p-8">
        <h2 className="text-3xl font-semibold text-green-700 mb-6 text-center">Liste des facilitÃ©s</h2>

        {loading ? (
          <p className="text-center text-gray-500">Chargement...</p>
        ) : facilites.length === 0 ? (
          <p className="text-center text-gray-500">Aucune facilitÃ© disponible pour le moment.</p>
        ) : (
          <ul className="space-y-4">
            {facilites.map((f) => (
              <li
                key={f.id_type_projet || f.id}
                className="border border-gray-300 rounded-md p-4 flex justify-between items-center"
              >
                <div>
                  <p className="font-semibold text-gray-800">{f.nom_facilite}</p>
                  <p className="text-sm text-gray-500">
                    Auteur : {f.auteur || 'N/A'} â€” CrÃ©Ã© le {f.date_creation || 'N/A'}
                  </p>
                </div>
                <div className="text-green-600 font-mono">{f.id_type_projet || f.id}</div>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default Facilite;




