import React from 'react';
import { FiUpload, FiEye, FiEdit, FiDatabase } from 'react-icons/fi';

function CommentCaMarche() {
  const steps = [
    {
      icon: <FiUpload className="text-green-600 text-3xl" />,
      title: 'Importer un fichier Excel',
      description: 'Sélectionnez le fichier à partir de votre appareil. Nous acceptons les formats .xlsx et .xls.',
    },
    {
      icon: <FiEye className="text-green-600 text-3xl" />,
      title: 'Prévisualiser les données',
      description: 'Avant l’importation, vous pouvez visualiser vos données directement dans l’interface.',
    },
    {
      icon: <FiEdit className="text-green-600 text-3xl" />,
      title: 'Modifier les données',
      description: 'Ajoutez, corrigez ou supprimez des lignes selon vos besoins. Vos modifications sont prises en compte en temps réel.',
    },
    {
      icon: <FiDatabase className="text-green-600 text-3xl" />,
      title: 'Importer dans la base de données',
      description: 'Une fois vos données prêtes, cliquez sur "Importer dans la base". Vos informations seront enregistrées dans PostgreSQL.',
    },
  ];

  return (
    <div className="min-h-screen flex flex-col items-center justify-start bg-white px-6 py-12">
      <h1 className="text-4xl font-bold text-green-700 mb-10 text-center">
        Comment ça marche ?
      </h1>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-8 w-full max-w-6xl">
        {steps.map((step, index) => (
          <div
            key={index}
            className="bg-green-50 rounded-xl p-6 shadow hover:shadow-lg transition duration-300 transform hover:-translate-y-1"
          >
            <div className="flex items-center gap-4 mb-4">
              <div className="bg-green-100 p-3 rounded-full">
                {step.icon}
              </div>
              <h2 className="text-xl font-semibold text-green-700">
                {step.title}
              </h2>
            </div>
            <p className="text-gray-600">{step.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CommentCaMarche;
