import React, { useState } from 'react';
import { motion } from 'framer-motion';

const typesFacilites = [
  {
    nom: 'Bonification',
    description: "Réduction d'intérêt ou coût pris en charge partiellement par l'État."
  },
  {
    nom: 'Subvention',
    description: "Aide financière directe octroyée pour soutenir un projet."
  },
  {
    nom: 'Accompagnement Technique',
    description: "Soutien non financier comme la formation ou le conseil."
  },
  {
    nom: 'Garantie',
    description: "Engagement d’un tiers à couvrir un risque de non-remboursement."
  }
];

function Facilite() {
  const [faciliteChoisie, setFaciliteChoisie] = useState('');
  const [accordionOuvert, setAccordionOuvert] = useState(false);

  const handleChoix = (e) => {
    setFaciliteChoisie(e.target.value);
  };

  return (
    <div className="space-y-10">
      <h1 className="text-3xl font-bold text-green-600">Facilité</h1>

      {/* Volet 1 */}
      <div className="bg-white rounded-2xl shadow p-6 border border-gray-200">
        <h2 className="text-xl font-semibold text-gray-800 mb-4">Choisir le type de facilité</h2>

        <select
          className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
          onChange={handleChoix}
          value={faciliteChoisie}
        >
          <option value="">-- Sélectionner une facilité --</option>
          {typesFacilites.map((facilite, index) => (
            <option key={index} value={facilite.nom}>{facilite.nom}</option>
          ))}
        </select>

        {faciliteChoisie && (
          <p className="mt-4 text-green-700 font-medium">
            Facilité choisie : <span className="font-bold">{faciliteChoisie}</span>
          </p>
        )}
      </div>

      {/* Volet 2 - Accordéon */}
      <div className="bg-white rounded-2xl shadow border border-gray-200">
        <button
          onClick={() => setAccordionOuvert(!accordionOuvert)}
          className="w-full text-left p-6 text-xl font-semibold text-gray-800 hover:bg-gray-100 transition rounded-t-2xl"
        >
          Découvrir nos types de facilités
        </button>

        {accordionOuvert && (
          <motion.div
            initial={{ height: 0, opacity: 0 }}
            animate={{ height: 'auto', opacity: 1 }}
            transition={{ duration: 0.3 }}
            className="px-6 pb-6"
          >
            <ul className="space-y-4">
              {typesFacilites.map((item, index) => (
                <li key={index} className="bg-gray-50 p-4 rounded-lg border border-gray-100">
                  <h3 className="text-green-600 font-semibold text-lg">{item.nom}</h3>
                  <p className="text-gray-600 text-sm mt-1">{item.description}</p>
                </li>
              ))}
            </ul>
          </motion.div>
        )}
      </div>
    </div>
  );
}

export default Facilite;
