import React, { useState } from "react";
import { FiUpload, FiSave, FiDatabase } from "react-icons/fi";
import * as XLSX from "xlsx";
import DataTable from "react-data-table-component";

function Importation() {
  const [selectedType, setSelectedType] = useState("");
  const [confirmedType, setConfirmedType] = useState(null);
  const [file, setFile] = useState(null);
  const [tableData, setTableData] = useState([]);
  const [columns, setColumns] = useState([]);
  const [editedData, setEditedData] = useState([]);
  const [importStatus, setImportStatus] = useState(null);

  const projectTypes = ["Infrastructures", "Équipements", "Formations", "Autres"];

  const handleTypeChange = (e) => {
    setSelectedType(e.target.value);
  };

  const handleConfirmType = () => {
    if (selectedType) {
      setConfirmedType(selectedType);
    }
  };

  const handleReset = () => {
    setConfirmedType(null);
    setSelectedType("");
    setFile(null);
    setTableData([]);
    setColumns([]);
    setEditedData([]);
    setImportStatus(null);
  };

  const handleFileUpload = (e) => {
    const uploadedFile = e.target.files[0];
    setFile(uploadedFile);
    const reader = new FileReader();
    reader.onload = (evt) => {
      const bstr = evt.target.result;
      const wb = XLSX.read(bstr, { type: "binary" });
      const wsName = wb.SheetNames[0];
      const ws = wb.Sheets[wsName];
      const data = XLSX.utils.sheet_to_json(ws, { header: 1 });

      const headers = data[0];
      const rows = data.slice(1).map((row, rowIndex) =>
        Object.fromEntries(headers.map((header, colIndex) => [header, row[colIndex]]))
      );

      setColumns(headers.map((col) => ({
        name: col,
        selector: (row) => row[col],
        sortable: true,
        cell: (row, index, column) => (
          <input
            type="text"
            className="w-full border rounded px-1"
            value={row[col] || ""}
            onChange={(e) => handleCellEdit(index, col, e.target.value)}
          />
        )
      })));
      setTableData(rows);
      setEditedData(rows);
    };
    reader.readAsBinaryString(uploadedFile);
  };

  const handleCellEdit = (rowIndex, column, value) => {
    const updatedData = [...editedData];
    updatedData[rowIndex][column] = value;
    setEditedData(updatedData);
  };

  const handleDownload = () => {
    const ws = XLSX.utils.json_to_sheet(editedData);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Données");
    XLSX.writeFile(wb, "copie_modifiee.xlsx");
  };

  const handleImportToDB = async () => {
    try {
      const response = await fetch("http://localhost:5000/import_excel", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ data: editedData, type_projet: confirmedType }),
      });

      const result = await response.json();
      setImportStatus({ success: result.success, message: result.message });
    } catch (error) {
      setImportStatus({ success: false, message: "Erreur lors de l'importation." });
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 py-12 px-4">
      <div className="max-w-5xl mx-auto bg-white shadow-md rounded-xl p-8">
        <h2 className="text-3xl font-bold text-center text-gray-800 mb-10">
          Importation de fichier Excel
        </h2>

        {/* Sélection du type de projet */}
        {!confirmedType ? (
          <div className="mb-10 mt-10">
            <h3 className="text-xl font-semibold mb-6 text-gray-700 text-center">
              Choisissez le type de projet
            </h3>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 justify-center">
              {projectTypes.map((type) => (
                <label
                  key={type}
                  className={`cursor-pointer border rounded-xl p-4 text-center text-gray-700 font-medium hover:shadow-lg transition ${
                    selectedType === type ? "bg-blue-100 border-blue-500" : "bg-white"
                  }`}
                >
                  <input
                    type="radio"
                    name="type_projet"
                    value={type}
                    className="hidden"
                    onChange={handleTypeChange}
                    checked={selectedType === type}
                  />
                  {type}
                </label>
              ))}
            </div>
            <div className="text-center mt-6">
              <button
                onClick={handleConfirmType}
                disabled={!selectedType}
                className={`px-6 py-2 rounded-lg font-semibold text-white ${
                  selectedType ? "bg-blue-600 hover:bg-blue-700" : "bg-gray-400 cursor-not-allowed"
                }`}
              >
                Valider
              </button>
            </div>
          </div>
        ) : (
          <>
            <div className="text-center mb-6">
              <p className="text-lg text-gray-600">
                <span className="font-semibold">Type de projet sélectionné :</span>{" "}
                {confirmedType}
              </p>
              <button
                onClick={handleReset}
                className="mt-2 text-sm text-red-600 hover:underline"
              >
                Modifier la sélection
              </button>
            </div>

            {/* Upload fichier Excel */}
            <div className="mb-8">
              <label className="block mb-2 font-semibold text-gray-700">
                Sélectionnez un fichier Excel :
              </label>
              <input
                type="file"
                accept=".xlsx, .xls"
                onChange={handleFileUpload}
                className="w-full p-2 border border-gray-300 rounded-lg"
              />
            </div>

            {/* Tableau de données */}
            {columns.length > 0 && (
              <div className="space-y-6">
                <DataTable
                  columns={columns}
                  data={editedData}
                  pagination
                  highlightOnHover
                  dense
                  customStyles={{
                    rows: {
                      style: {
                        minHeight: '48px',
                      },
                    },
                    headCells: {
                      style: {
                        fontWeight: 'bold',
                        backgroundColor: '#f1f5f9',
                      },
                    },
                  }}
                />

                <div className="flex justify-end gap-4">
                  <button
                    onClick={handleDownload}
                    className="bg-blue-700 hover:bg-blue-800 text-white px-5 py-2 rounded flex items-center gap-2"
                  >
                    <FiSave /> Télécharger la copie modifiée
                  </button>
                  <button
                    onClick={handleImportToDB}
                    className="bg-green-700 hover:bg-green-800 text-white px-5 py-2 rounded flex items-center gap-2"
                  >
                    <FiDatabase /> Importer dans la base
                  </button>
                </div>

                {/* Résultat de l'import */}
                {importStatus && (
                  <p
                    className={`mt-6 text-center font-semibold ${
                      importStatus.success ? "text-green-700" : "text-red-700"
                    }`}
                  >
                    {importStatus.message}
                  </p>
                )}
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
}

export default Importation;
