import React from 'react';
import { FiUpload, FiEye, FiEdit, FiDatabase } from 'react-icons/fi';

function CommentCaMarche() {
  const steps = [
    {
      icon: <FiUpload className="text-green-600 text-3xl" />,
      title: 'Importer un fichier Excel',
      description: 'Téléversez facilement vos fichiers Excel (.xlsx, .xls) via l’interface intuitive.',
    },
    {
      icon: <FiEye className="text-green-600 text-3xl" />,
      title: 'Prévisualiser les données',
      description: 'Consultez vos données avant l’importation et assurez-vous que tout est conforme.',
    },
    {
      icon: <FiEdit className="text-green-600 text-3xl" />,
      title: 'Modifier au besoin',
      description: 'Corrigez, supprimez ou ajoutez des lignes avant de valider l’importation.',
    },
    {
      icon: <FiDatabase className="text-green-600 text-3xl" />,
      title: 'Importer dans la base',
      description: 'Une fois prêt, importez vos données dans la base PostgreSQL en un seul clic.',
    },
  ];

  return (
    <div className="min-h-screen px-4 md:px-8 py-12 bg-white">
      {/* Titre principal détaché */}
      <div className="text-center mb-16">
        <h1 className="text-4xl md:text-5xl font-extrabold text-green-700 mb-1">
          Comment ça marche ?
        </h1>
        <p className="text-gray-500 text-lg max-w-2xl mx-auto">
          Voici les différentes étapes pour utiliser l’outil d’importation de données avec simplicité et efficacité.
        </p>
      </div>

      {/* Étapes */}
      <div className="max-w-6xl mx-auto grid gap-8 grid-cols-1 md:grid-cols-2">
        {steps.map((step, index) => (
          <div
            key={index}
            className="bg-green-50 p-6 rounded-2xl shadow-md hover:shadow-lg transition duration-300 transform hover:-translate-y-1"
          >
            <div className="flex items-center gap-4 mb-4">
              <div className="bg-green-100 p-3 rounded-full">
                {step.icon}
              </div>
              <h2 className="text-xl font-semibold text-green-700">
                {step.title}
              </h2>
            </div>
            <p className="text-gray-600 text-sm leading-relaxed">
              {step.description}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CommentCaMarche;
