import React, { useState, useRef } from 'react';
import { FiUser, FiMail, FiEdit3, FiCheck, FiX, FiCamera, FiImage } from 'react-icons/fi';

function MonCompte() {
  const [nom, setNom] = useState("Dupont");
  const [prenom, setPrenom] = useState("Sergio");
  const [email, setEmail] = useState("sergio@fnda.bj");
  const [editField, setEditField] = useState(null);

  const [tempValues, setTempValues] = useState({
    nom,
    prenom,
    email,
  });

  const [avatar, setAvatar] = useState(null);
  const [isCameraOpen, setIsCameraOpen] = useState(false);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const openCamera = async () => {
    setIsCameraOpen(true);
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        videoRef.current.play();
      }
    } catch (err) {
      alert("Erreur d'accès à la caméra : " + err.message);
      setIsCameraOpen(false);
    }
  };

  const capturePhoto = () => {
    if (!videoRef.current || !canvasRef.current) return;
    const video = videoRef.current;
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    context.drawImage(video, 0, 0, canvas.width, canvas.height);
    const imageDataUrl = canvas.toDataURL('image/png');
    setAvatar(imageDataUrl);

    const stream = video.srcObject;
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
    }
    setIsCameraOpen(false);
  };

  const cancelCamera = () => {
    if (videoRef.current) {
      const stream = videoRef.current.srcObject;
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
      }
    }
    setIsCameraOpen(false);
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    if (!file.type.startsWith("image/")) {
      alert("Veuillez choisir un fichier image valide.");
      return;
    }
    const reader = new FileReader();
    reader.onload = () => {
      setAvatar(reader.result);
    };
    reader.readAsDataURL(file);
  };

  const handleEditClick = (field) => {
    setEditField(field);
    setTempValues(prev => ({ ...prev, [field]: eval(field) }));
  };

  const handleCancel = () => setEditField(null);

  const handleSave = () => {
    if (editField === "nom") setNom(tempValues.nom);
    else if (editField === "prenom") setPrenom(tempValues.prenom);
    else if (editField === "email") setEmail(tempValues.email);
    setEditField(null);
  };

  const renderField = (label, fieldKey, value, icon) => {
    const isEditing = editField === fieldKey;
    return (
      <div className="flex flex-col gap-2 bg-white p-5 rounded-xl shadow border">
        <div className="flex items-center gap-3 mb-1">
          {icon}
          <h4 className="text-gray-600 font-medium">{label}</h4>
        </div>
        {!isEditing ? (
          <div className="flex justify-between items-center">
            <p className="text-lg font-semibold text-gray-800">{value}</p>
            <button
              onClick={() => handleEditClick(fieldKey)}
              className="text-green-600 hover:text-green-800"
              title="Modifier"
            >
              <FiEdit3 size={20} />
            </button>
          </div>
        ) : (
          <>
            <input
              type="text"
              value={tempValues[fieldKey]}
              onChange={(e) => setTempValues({ ...tempValues, [fieldKey]: e.target.value })}
              className="input-style"
            />
            <div className="flex gap-2 mt-2">
              <button
                onClick={handleSave}
                className="text-green-600 hover:text-green-800"
                title="Enregistrer"
              >
                <FiCheck size={20} />
              </button>
              <button
                onClick={handleCancel}
                className="text-red-500 hover:text-red-700"
                title="Annuler"
              >
                <FiX size={20} />
              </button>
            </div>
          </>
        )}
      </div>
    );
  };

  return (
    <div className="w-full min-h-screen bg-gray-50 flex flex-col items-center px-4 py-10">
      <h2 className="text-4xl font-bold text-green-700 mb-8">Mon compte</h2>

      <div className="w-full max-w-4xl bg-white rounded-3xl shadow-xl p-6 md:p-10 flex flex-col md:flex-row gap-10 items-center md:items-start">
        {/* Avatar + Contrôles */}
        <div className="flex flex-col items-center gap-4">
          <div
            onClick={openCamera}
            className="cursor-pointer w-32 h-32 md:w-40 md:h-40 rounded-full overflow-hidden border-4 border-green-100 shadow-md flex items-center justify-center bg-gray-100"
            title="Cliquer pour prendre une photo"
          >
            {avatar ? (
              <img src={avatar} alt="Avatar utilisateur" className="object-cover w-full h-full" />
            ) : (
              <FiUser className="text-green-600 w-20 h-20 md:w-24 md:h-24 p-6" />
            )}
          </div>

          <label
            htmlFor="fileInput"
            className="cursor-pointer flex items-center gap-2 text-green-600 hover:text-green-800 font-medium"
          >
            <FiImage size={24} /> Choisir une image depuis la galerie
          </label>
          <input
            type="file"
            id="fileInput"
            accept="image/*"
            onChange={handleFileChange}
            className="hidden"
          />

          {isCameraOpen && (
            <div className="fixed inset-0 bg-black bg-opacity-70 flex flex-col items-center justify-center z-50 p-4">
              <video ref={videoRef} className="rounded-lg max-w-full max-h-[70vh]" />
              <div className="mt-4 flex gap-4">
                <button onClick={capturePhoto} className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">
                  Prendre la photo
                </button>
                <button onClick={cancelCamera} className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700">
                  Annuler
                </button>
              </div>
              <canvas ref={canvasRef} className="hidden" />
            </div>
          )}
        </div>

        {/* Formulaire utilisateur */}
        <div className="flex flex-col flex-grow gap-6 w-full max-w-md">
          {renderField("Nom", "nom", nom, <FiUser size={24} className="text-green-600" />)}
          {renderField("Prénom", "prenom", prenom, <FiUser size={24} className="text-green-600" />)}
          {renderField("Adresse email", "email", email, <FiMail size={24} className="text-green-600" />)}
        </div>
      </div>
    </div>
  );
}

export default MonCompte;
