import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { FiPlus, FiMinus } from 'react-icons/fi';

const typesFacilites = [
  {
    nom: 'Bonification',
    description: 'Un soutien financier accordé pour alléger les charges du promoteur.',
  },
  {
    nom: 'Subvention',
    description: "Aide financière directe pour encourager l'investissement dans certains secteurs.",
  },
  {
    nom: 'Garantie',
    description: "Caution ou assurance fournie pour sécuriser les engagements de financement.",
  },
  {
    nom: 'Avance remboursable',
    description: "Financement temporaire à rembourser après une certaine période.",
  },
];

function Facilite() {
  const [choixEnCours, setChoixEnCours] = useState('');
  const [choixValide, setChoixValide] = useState('');
  const [afficherInfos, setAfficherInfos] = useState(false);

  const handleValidation = () => {
    setChoixValide(choixEnCours);
  };

  return (
    <div className="p-6 md:p-10 space-y-10">
        <h1 className="text-3xl font-bold text-center text-green-700 mb-8">
            Nos Facilités
        </h1>


      {/* Module 1 - Choix de facilité */}
      <div className="bg-white p-6 rounded-2xl shadow-md">
        <h2 className="text-2xl font-semibold text-center mb-6 text-green-600">Choisir le type de facilité</h2>

        <div className="flex flex-col md:flex-row md:items-center gap-4">
          <select
            value={choixEnCours}
            onChange={(e) => setChoixEnCours(e.target.value)}
            className="flex-1 p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
          >
            <option value="">-- Sélectionnez une facilité --</option>
            {typesFacilites.map((facilite, idx) => (
              <option key={idx} value={facilite.nom}>{facilite.nom}</option>
            ))}
          </select>

          <button
            onClick={handleValidation}
            className="bg-green-600 text-white px-6 py-3 rounded-lg hover:bg-green-700 transition font-medium"
          >
            Valider
          </button>
        </div>

        {choixValide && (
          <p className="mt-4 text-green-700 font-semibold">
            Facilité choisie : <span className="underline">{choixValide}</span>
          </p>
        )}
      </div>

      {/* Module 2 - Découverte des facilités */}
      <div className="bg-white p-6 rounded-2xl shadow-md">
        <div
          className="flex justify-between items-center cursor-pointer mb-2"
          onClick={() => setAfficherInfos(!afficherInfos)}
        >
          <h2 className="text-2xl font-semibold text-center text-green-600 w-full">Découvrir nos types de facilités</h2>
          <span className="text-green-600 ml-4">
            {afficherInfos ? <FiMinus size={24} /> : <FiPlus size={24} />}
          </span>
        </div>

        <AnimatePresence>
          {afficherInfos && (
            <motion.div
              initial={{ opacity: 0, height: 0 }}
              animate={{ opacity: 1, height: 'auto' }}
              exit={{ opacity: 0, height: 0 }}
              transition={{ duration: 0.4 }}
              className="overflow-hidden space-y-4 mt-4"
            >
              {typesFacilites.map((facilite, index) => (
                <div
                  key={index}
                  className="p-4 bg-gray-50 rounded-lg border border-gray-200 hover:shadow"
                >
                  <h3 className="text-lg font-semibold text-green-700">{facilite.nom}</h3>
                  <p className="text-gray-700 text-sm mt-1">{facilite.description}</p>
                </div>
              ))}
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
}

export default Facilite;
