import React, { useState } from 'react';
import { FiUser, FiMail, FiLock, FiEdit3, FiCheck, FiX } from 'react-icons/fi';

function MonCompte() {
  const [username, setUsername] = useState("Utilisateur");
  const [email, setEmail] = useState("utilisateur@example.com");
  const [password, setPassword] = useState("********");

  const [editField, setEditField] = useState(null);
  const [tempValues, setTempValues] = useState({
    username,
    email,
    password: '',
  });

  const handleEditClick = (field) => {
    setEditField(field);
    setTempValues({ ...tempValues, [field]: field === "password" ? "" : eval(field) });
  };

  const handleCancel = () => {
    setEditField(null);
  };

  const handleSave = () => {
    if (editField === "username") setUsername(tempValues.username);
    if (editField === "email") setEmail(tempValues.email);
    if (editField === "password") setPassword("********"); // Pour affichage, pas stockage
    setEditField(null);
  };

  const renderField = (label, fieldKey, value, icon) => {
    const isEditing = editField === fieldKey;

    return (
      <div className="flex items-center justify-between p-4 bg-white rounded-lg shadow">
        <div className="flex items-center gap-4 flex-1">
          {icon}
          <div className="flex-1">
            <p className="text-sm text-gray-500">{label}</p>
            {isEditing ? (
              <input
                type={fieldKey === "password" ? "password" : "text"}
                value={tempValues[fieldKey]}
                onChange={(e) =>
                  setTempValues({ ...tempValues, [fieldKey]: e.target.value })
                }
                className="mt-1 w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-green-500"
              />
            ) : (
              <p className="text-lg font-semibold">{value}</p>
            )}
          </div>
        </div>

        {isEditing ? (
          <div className="flex gap-2 ml-4">
            <button
              onClick={handleSave}
              className="text-green-600 hover:text-green-800"
              title="Enregistrer"
            >
              <FiCheck size={20} />
            </button>
            <button
              onClick={handleCancel}
              className="text-red-500 hover:text-red-700"
              title="Annuler"
            >
              <FiX size={20} />
            </button>
          </div>
        ) : (
          <button
            onClick={() => handleEditClick(fieldKey)}
            className="text-green-600 hover:text-green-800"
            title="Modifier"
          >
            <FiEdit3 size={20} />
          </button>
        )}
      </div>
    );
  };

  return (
    <div className="w-full h-screen bg-white flex flex-col items-center justify-start p-6 overflow-auto">
      <h2 className="text-3xl md:text-4xl font-bold text-green-700 mb-8 animate-slide-in-left">
        Mon compte
      </h2>

      <div className="w-full max-w-xl p-6 bg-gray-50 rounded-2xl shadow-lg space-y-6">
        {renderField("Nom dâ€™utilisateur", "username", username, <FiUser className="text-green-600" size={24} />)}
        {renderField("Adresse email", "email", email, <FiMail className="text-green-600" size={24} />)}
        {renderField("Mot de passe", "password", password, <FiLock className="text-green-600" size={24} />)}
      </div>
    </div>
  );
}

export default MonCompte;
