import React, { useState, useEffect } from 'react';

export default function MonCompte() {
  // États pour les infos utilisateur
  const [userData, setUserData] = useState({
    nom: '',
    prenom: '',
    email: '',
    photoProfil: null, // à adapter si tu stockes ça différemment
  });

  // États pour l’édition
  const [isEditing, setIsEditing] = useState(false);
  const [editedData, setEditedData] = useState({
    nom: '',
    prenom: '',
    email: '',
  });

  // Chargement des infos user connecté au chargement composant
  useEffect(() => {
    fetch('/profile', { method: 'GET', credentials: 'include' })
      .then(res => {
        if (!res.ok) throw new Error('Erreur chargement profil');
        return res.json();
      })
      .then(data => {
        setUserData({
          nom: data.nom,
          prenom: data.prenom,
          email: data.email,
          photoProfil: data.photoProfil || null, // adapte si photo dans API
        });
        setEditedData({
          nom: data.nom,
          prenom: data.prenom,
          email: data.email,
        });
      })
      .catch(err => {
        console.error(err);
        alert('Impossible de charger le profil utilisateur.');
      });
  }, []);

  // Gestion input éditable
  function handleChange(e) {
    setEditedData({ ...editedData, [e.target.name]: e.target.value });
  }

  // Annuler modif -> remettre les données initiales + quitter édition
  function handleCancel() {
    setEditedData({
      nom: userData.nom,
      prenom: userData.prenom,
      email: userData.email,
    });
    setIsEditing(false);
  }

  // Confirmer modif -> appel PUT API
  function handleConfirm() {
    fetch('/profile', {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      credentials: 'include',
      body: JSON.stringify(editedData),
    })
      .then(res => {
        if (!res.ok) throw new Error('Erreur mise à jour profil');
        return res.json();
      })
      .then(() => {
        setUserData(editedData);
        setIsEditing(false);
        alert('Profil mis à jour avec succès.');
      })
      .catch(err => {
        console.error(err);
        alert('Erreur lors de la mise à jour.');
      });
  }

  return (
    <div className="moncompte-container">
      <h2>Mon Compte</h2>

      {/* Photo profil si tu as */}
      {userData.photoProfil ? (
        <img
          src={userData.photoProfil}
          alt="Photo de profil"
          style={{ width: '120px', height: '120px', borderRadius: '50%' }}
        />
      ) : (
        <div
          style={{
            width: '120px',
            height: '120px',
            borderRadius: '50%',
            backgroundColor: '#ccc',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '2rem',
            color: '#666',
          }}
        >
          ?
        </div>
      )}

      <form
        onSubmit={e => {
          e.preventDefault();
          if (isEditing) handleConfirm();
        }}
        style={{ marginTop: '20px' }}
      >
        <label>
          Nom :
          <input
            type="text"
            name="nom"
            value={editedData.nom}
            onChange={handleChange}
            disabled={!isEditing}
            style={{ marginLeft: '10px' }}
          />
        </label>

        <br />

        <label>
          Prénom :
          <input
            type="text"
            name="prenom"
            value={editedData.prenom}
            onChange={handleChange}
            disabled={!isEditing}
            style={{ marginLeft: '10px' }}
          />
        </label>

        <br />

        <label>
          Email :
          <input
            type="email"
            name="email"
            value={editedData.email}
            onChange={handleChange}
            disabled={!isEditing}
            style={{ marginLeft: '10px' }}
          />
        </label>

        <br />

        {!isEditing && (
          <button
            type="button"
            onClick={() => setIsEditing(true)}
            style={{ marginTop: '15px' }}
          >
            Modifier
          </button>
        )}

        {isEditing && (
          <>
            <button
              type="button"
              onClick={handleCancel}
              style={{ marginTop: '15px', marginRight: '10px' }}
            >
              Annuler les modifications
            </button>

            <button
              type="submit"
              style={{ marginTop: '15px' }}
            >
              Confirmer
            </button>
          </>
        )}
      </form>
    </div>
  );
}
