import React, { useState } from 'react';
import logo from '../assets/logo.png'; // chemin pour le logo

export default function SignUp() {
  const [formData, setFormData] = useState({
    nom: '',
    prenom: '',
    email: '',
    mot_de_passe: '',
  });
  const [showPassword, setShowPassword] = useState(false);
  const [message, setMessage] = useState({ text: '', color: '' });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const togglePasswordVisibility = () => {
    setShowPassword(prev => !prev);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage({ text: '', color: '' });
    setLoading(true);

    try {
      const response = await fetch('http://localhost:5000/auth/signup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      const result = await response.json();

      if (response.ok) {
        setMessage({ text: 'Inscription r√©ussie ! Connectez-vous maintenant.', color: 'text-emerald-600' });
        setFormData({ nom: '', prenom: '', email: '', mot_de_passe: '' });
        setTimeout(() => {
          window.location.href = '/signin';
        }, 2000);
      } else {
        setMessage({ text: result.error || 'Une erreur est survenue.', color: 'text-red-600' });
      }
    } catch {
      setMessage({ text: 'Impossible de contacter le serveur.', color: 'text-red-600' });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-gray-200 flex items-center justify-center min-h-screen px-4">
      <div className="bg-white rounded-2xl shadow-lg w-full max-w-5xl flex flex-col md:flex-row overflow-hidden">
        {/* Left part */}
        <div className="hidden md:flex md:w-1/2 bg-gradient-to-br from-emerald-50 to-gray-100 items-center justify-center p-10 rounded-l-2xl shadow-inner">
          <div className="text-center max-w-md">
            <h2 className="text-3xl font-extrabold text-gray-700 mb-4">Bienvenue !</h2>
            <p className="text-gray-600 text-lg font-semibold">
              Inscrivez-vous d√®s maintenant pour profiter pleinement de tous nos services.
            </p>
            <img
              src="/img/dec.png"
              alt="Inscription"
              className="mb-6 w-[90%] h-72 mx-auto mt-12 object-contain"
            />
          </div>
        </div>

        {/* Right part - form */}
        <div className="w-full md:w-1/2 p-8">
          {/* Logo */}
          <div className="flex justify-center mb-6">
            <img src={logo} alt="Logo du site" className="h-24 w-auto" />
          </div>

          <h1 className="text-2xl font-semibold text-center text-gray-700 mb-6">
            Portail d'inscription
          </h1>

          <form onSubmit={handleSubmit} className="space-y-5" noValidate>
            <div>
              <label htmlFor="nom" className="block text-gray-700 font-medium">
                Nom
              </label>
              <input
                type="text"
                id="nom"
                name="nom"
                placeholder="ABDALLAH"
                required
                value={formData.nom}
                onChange={handleChange}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-emerald-500"
              />
            </div>

            <div>
              <label htmlFor="prenom" className="block text-gray-700 font-medium">
                Pr√©nom
              </label>
              <input
                type="text"
                id="prenom"
                name="prenom"
                placeholder="Karim"
                required
                value={formData.prenom}
                onChange={handleChange}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-emerald-500"
              />
            </div>

            <div>
              <label htmlFor="email" className="block text-gray-700 font-medium">
                Email
              </label>
              <input
                type="email"
                id="email"
                name="email"
                placeholder="sergio@fnda.bj"
                required
                value={formData.email}
                onChange={handleChange}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-emerald-500"
              />
            </div>

            <div>
              <label htmlFor="mot_de_passe" className="block text-gray-700 font-medium">
                Mot de passe
              </label>
              <div className="relative">
                <input
                  id="mot_de_passe"
                  type={showPassword ? 'text' : 'password'}
                  name="mot_de_passe"
                  placeholder="Mot de passe"
                  required
                  value={formData.mot_de_passe}
                  onChange={handleChange}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-emerald-500"
                />
                <button
                  type="button"
                  onClick={togglePasswordVisibility}
                  className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-500 focus:outline-none"
                  aria-label={showPassword ? 'Masquer le mot de passe' : 'Afficher le mot de passe'}
                >
                  {showPassword ? 'üôà' : 'üëÅÔ∏è'}
                </button>
              </div>
            </div>

            <button
              type="submit"
              disabled={loading}
              className={`w-full py-2 rounded-lg font-semibold transition-colors ${
                loading
                  ? 'bg-gray-400 cursor-not-allowed text-white'
                  : 'bg-emerald-600 hover:bg-teal-800 text-white'
              }`}
            >
              {loading ? 'Chargement...' : "S'inscrire"}
            </button>
          </form>

          {message.text && (
            <p className={`mt-4 text-center font-semibold ${message.color}`}>
              {message.text}
            </p>
          )}

          <p className="mt-4 text-center text-gray-600">
            D√©j√† un compte ?{' '}
            <a href="/signin" className="text-emerald-600 hover:underline">
              Se connecter
            </a>
          </p>
        </div>
      </div>
    </div>
  );
}
