import React, { useState, useEffect } from 'react';
import { FiUser, FiMail, FiEdit3, FiCheck, FiX } from 'react-icons/fi';

function MonCompte() {
  const [userData, setUserData] = useState({ nom: '', prenom: '', email: '' });
  const [editMode, setEditMode] = useState(false);
  const [tempValues, setTempValues] = useState({ nom: '', prenom: '', email: '' });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Charger les données utilisateur à l'ouverture
    fetch('/profile')
      .then(res => {
        if (!res.ok) throw new Error("Utilisateur non connecté");
        return res.json();
      })
      .then(data => {
        setUserData(data);
        setTempValues(data);
        setLoading(false);
      })
      .catch(err => {
        alert("Erreur lors du chargement des données : " + err.message);
        setLoading(false);
      });
  }, []);

  const handleEditClick = () => {
    setEditMode(true);
  };

  const handleCancel = () => {
    setTempValues(userData); // Restaure les valeurs initiales
    setEditMode(false);
  };

  const handleConfirm = () => {
    // Appel API pour mise à jour des données utilisateur
    fetch('/profile', {
      method: 'PUT', // ou POST selon ton API
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(tempValues),
    })
      .then(res => {
        if (!res.ok) throw new Error("Erreur lors de la mise à jour");
        return res.json();
      })
      .then(updatedUser => {
        setUserData(updatedUser);
        setTempValues(updatedUser);
        setEditMode(false);
        alert("Profil mis à jour avec succès !");
      })
      .catch(err => alert(err.message));
  };

  if (loading) return <p>Chargement...</p>;

  return (
    <div className="w-full min-h-screen bg-gray-50 flex flex-col items-center px-4 py-10">
      <h2 className="text-4xl font-bold text-green-700 mb-8">Mon compte</h2>

      <div className="w-full max-w-4xl bg-white rounded-3xl shadow-xl p-6 md:p-10 flex flex-col gap-10">
        {/* Nom */}
        <div className="flex flex-col gap-2 bg-white p-5 rounded-xl shadow border">
          <div className="flex items-center gap-3 mb-1">
            <FiUser className="text-green-600" size={24} />
            <h4 className="text-gray-600 font-medium">Nom</h4>
          </div>
          {editMode ? (
            <input
              type="text"
              value={tempValues.nom}
              onChange={e => setTempValues({ ...tempValues, nom: e.target.value })}
              className="input-style"
            />
          ) : (
            <p className="text-lg font-semibold text-gray-800">{userData.nom}</p>
          )}
        </div>

        {/* Prénom */}
        <div className="flex flex-col gap-2 bg-white p-5 rounded-xl shadow border">
          <div className="flex items-center gap-3 mb-1">
            <FiUser className="text-green-600" size={24} />
            <h4 className="text-gray-600 font-medium">Prénom</h4>
          </div>
          {editMode ? (
            <input
              type="text"
              value={tempValues.prenom}
              onChange={e => setTempValues({ ...tempValues, prenom: e.target.value })}
              className="input-style"
            />
          ) : (
            <p className="text-lg font-semibold text-gray-800">{userData.prenom}</p>
          )}
        </div>

        {/* Email */}
        <div className="flex flex-col gap-2 bg-white p-5 rounded-xl shadow border">
          <div className="flex items-center gap-3 mb-1">
            <FiMail className="text-green-600" size={24} />
            <h4 className="text-gray-600 font-medium">Adresse email</h4>
          </div>
          {editMode ? (
            <input
              type="email"
              value={tempValues.email}
              onChange={e => setTempValues({ ...tempValues, email: e.target.value })}
              className="input-style"
            />
          ) : (
            <p className="text-lg font-semibold text-gray-800">{userData.email}</p>
          )}
        </div>

        {/* Boutons */}
        <div>
          {!editMode ? (
            <button
              onClick={handleEditClick}
              className="bg-green-600 text-white px-6 py-2 rounded hover:bg-green-700"
            >
              Modifier
            </button>
          ) : (
            <div className="flex gap-4">
              <button
                onClick={handleCancel}
                className="bg-red-600 text-white px-6 py-2 rounded hover:bg-red-700"
              >
                Annuler les modifications
              </button>
              <button
                onClick={handleConfirm}
                className="bg-green-600 text-white px-6 py-2 rounded hover:bg-green-700"
              >
                Confirmer
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default MonCompte;
