import React from 'react';
import { useNavigate } from 'react-router-dom';
import illustration from '../assets/logo.png'; // Assure-toi d’avoir cette image ou remplace le nom

function Home() {
  const navigate = useNavigate();

  return (
    <div className="flex flex-col md:flex-row items-center justify-between p-10 bg-white rounded-xl shadow-md">
      {/* Texte d'accueil */}
      <div className="md:w-1/2 mb-10 md:mb-0">
        <h1 className="text-4xl font-bold text-green-700 mb-6">
          Bienvenue sur la plateforme d'importation de données
        </h1>
        <p className="text-gray-700 text-lg mb-6">
          Importez, visualisez et gérez facilement vos données en quelques clics. Cette plateforme vous aide à centraliser et exploiter vos fichiers Excel sans effort.
        </p>
        <button
          onClick={() => navigate('/importer')}
          className="bg-green-600 text-white px-6 py-3 rounded-lg shadow hover:bg-green-700 transition-all"
        >
          Commencer l'importation
        </button>
      </div>

      {/* Illustration */}
      <div className="md:w-1/2 flex justify-center">
        <img
          src={illustration}
          alt="Illustration"
          className="w-full max-w-md animate-fade-in-up"
        />
      </div>
    </div>
  );
}

export default Home;
