import React from 'react';
import { Outlet, NavLink, useNavigate } from 'react-router-dom';
import { FiHome, FiHelpCircle, FiUpload, FiClock, FiUser, FiLogOut } from 'react-icons/fi';

function DashboardUserLayout() {
  const navigate = useNavigate();

  const linkClasses = ({ isActive }) =>
    `flex items-center gap-3 px-4 py-3 rounded-lg text-base font-medium transition-all ${
      isActive
        ? 'bg-green-600 text-white shadow'
        : 'text-gray-700 hover:bg-gray-200'
    }`;

  const handleLogout = () => {
    // Ici tu pourras ajouter la logique réelle de déconnexion
    console.log('Déconnexion...');
    navigate('/login'); // Redirection vers la page de connexion
  };

  return (
    <div className="flex min-h-screen bg-gray-100 font-sans">
      {/* Sidebar */}
      <aside className="w-64 bg-white shadow-lg p-6 flex flex-col justify-between">
        <div>
          <h1 className="text-2xl font-bold mb-10 text-green-700 text-center">Dashboard</h1>
          <nav className="space-y-4">
            <NavLink to="/" end className={linkClasses}>
              <FiHome className="text-lg" /> Accueil
            </NavLink>
            <NavLink to="/comment-ca-marche" className={linkClasses}>
              <FiHelpCircle className="text-lg" /> Comment ça marche
            </NavLink>
            <NavLink to="/importer" className={linkClasses}>
              <FiUpload className="text-lg" /> Importer des données
            </NavLink>
            <NavLink to="/historique" className={linkClasses}>
              <FiClock className="text-lg" /> Historique
            </NavLink>
            <NavLink to="/mon-compte" className={linkClasses}>
              <FiUser className="text-lg" /> Mon compte
            </NavLink>
          </nav>
        </div>

        {/* Bouton de déconnexion */}
        <button
          onClick={handleLogout}
          className="flex items-center gap-3 px-4 py-3 mt-6 rounded-lg text-base font-medium  hover:bg-green-600 hover:text-neutral-50 transition-all"
        >
          <FiLogOut className="text-lg" /> Se déconnecter
        </button>
      </aside>

      {/* Contenu principal */}
      <main className="flex-1 p-10">
        <Outlet />
      </main>
    </div>
  );
}

export default DashboardUserLayout;
