import React from 'react';
import { FiUpload, FiEye, FiEdit, FiDatabase } from 'react-icons/fi';

function CommentCaMarche() {
  const steps = [
    {
      icon: <FiUpload className="text-white text-2xl" />,
      title: 'Importer un fichier Excel',
      description: 'Téléversez facilement vos fichiers Excel (.xlsx, .xls) via l’interface intuitive.',
    },
    {
      icon: <FiEye className="text-white text-2xl" />,
      title: 'Prévisualiser les données',
      description: 'Consultez vos données avant l’importation et assurez-vous que tout est conforme.',
    },
    {
      icon: <FiEdit className="text-white text-2xl" />,
      title: 'Modifier au besoin',
      description: 'Corrigez, supprimez ou ajoutez des lignes avant de valider l’importation.',
    },
    {
      icon: <FiDatabase className="text-white text-2xl" />,
      title: 'Importer dans la base',
      description: 'Une fois prêt, importez vos données dans la base PostgreSQL en un seul clic.',
    },
  ];

  return (
    <div className="min-h-screen px-4 md:px-8 py-16 bg-white">
      {/* Titre principal */}
      <div className="text-center mb-20">
        <h1 className="text-4xl md:text-5xl font-extrabold text-green-700 mb-6">
          Comment ça marche ?
        </h1>
        <p className=" text-lg max-w-2xl mx-auto">
          Suivez ces étapes simples pour importer et gérer vos données en toute confiance.
        </p>
      </div>

      {/* Étapes */}
      <div className="max-w-6xl mx-auto grid gap-10 grid-cols-1 md:grid-cols-2">
        {steps.map((step, index) => (
          <div
            key={index}
            className="relative bg-green-50 p-6 pt-10 rounded-2xl shadow-md hover:shadow-lg transition duration-300 transform hover:-translate-y-1"
          >
            {/* Numéro ou icône + cercle stylisé */}
            <div className="absolute -top-5 left-6">
              <div className="w-12 h-12 flex items-center justify-center rounded-full bg-gradient-to-br from-green-500 to-green-700 ring-4 ring-white shadow-lg">
                {step.icon}
              </div>
            </div>

            <h2 className="text-xl font-semibold text-green-700 mt-4 pl-16">
              {index + 1}. {step.title}
            </h2>
            <p className="text-gray-600 text-sm leading-relaxed mt-2 pl-16">
              {step.description}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CommentCaMarche;
