import React, { useState } from 'react';
import { FiUpload, FiEdit, FiCheckCircle, FiSave, FiDatabase } from 'react-icons/fi';
import * as XLSX from 'xlsx';

function Importation() {
  // Étape 1 : choix du type de projet
  const [selectedTypes, setSelectedTypes] = useState([]);
  const [typeSelected, setTypeSelected] = useState(false);

  // Étape 2 : gestion fichier et données Excel
  const [file, setFile] = useState(null);
  const [data, setData] = useState([]);
  const [editMode, setEditMode] = useState(false);
  const [editFinished, setEditFinished] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [importStatus, setImportStatus] = useState(null);
  const rowsPerPage = 101;

  // Liste des types de projet (exemple, adapte à ton besoin)
  const projectTypes = [
    "Domaine A",
    "Domaine B",
    "Domaine C",
    "Domaine D",
  ];

  // Gestion changement checkbox
  const toggleType = (type) => {
    if (selectedTypes.includes(type)) {
      setSelectedTypes(selectedTypes.filter(t => t !== type));
    } else {
      setSelectedTypes([...selectedTypes, type]);
    }
  };

  // Validation du choix type projet
  const handleValidateTypes = () => {
    if (selectedTypes.length === 0) {
      alert("Veuillez sélectionner au moins un type de projet.");
      return;
    }
    setTypeSelected(true);
  };

  // Revenir au choix type projet
  const handleResetTypes = () => {
    setSelectedTypes([]);
    setTypeSelected(false);
    // Réinitialiser aussi tout le reste
    setFile(null);
    setData([]);
    setEditMode(false);
    setEditFinished(false);
    setImportStatus(null);
  };

  // Gestion fichier Excel (identique à ton code)
  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
    setEditFinished(false);
    setEditMode(false);
    setCurrentPage(1);
    setImportStatus(null);

    const reader = new FileReader();
    reader.onload = (evt) => {
      const bstr = evt.target.result;
      const wb = XLSX.read(bstr, { type: "binary" });
      const wsname = wb.SheetNames[0];
      const ws = wb.Sheets[wsname];
      const jsonData = XLSX.utils.sheet_to_json(ws, { header: 1 });
      setData(jsonData);
    };
    reader.readAsBinaryString(selectedFile);
  };

  // Edition tableau
  const handleEdit = () => {
    setEditMode(true);
    setEditFinished(false);
    setImportStatus(null);
  };

  const handleFinishEdit = () => {
    setEditMode(false);
    setEditFinished(true);
  };

  const handleCellChange = (rowIndex, colIndex, value) => {
    const newData = [...data];
    newData[rowIndex][colIndex] = value;
    setData(newData);
    setCurrentPage(1);
  };

  const handleSaveCopy = () => {
    const ws = XLSX.utils.aoa_to_sheet(data);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Feuille1");
    XLSX.writeFile(wb, "donnees_modifiees.xlsx");
  };

  // Envoi au backend Flask
  const handleImportToDB = async () => {
    if (!file) {
      setImportStatus({ success: false, message: "Veuillez sélectionner un fichier avant d'importer." });
      return;
    }

    try {
      const formData = new FormData();
      formData.append("file", file);

      const response = await fetch("http://localhost:5000/auth/import_excel", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Erreur serveur : ${errorText}`);
      }

      const result = await response.json();
      setImportStatus({ success: true, message: result.message || "Importation réussie !" });
    } catch (error) {
      setImportStatus({ success: false, message: error.message || "Erreur lors de l'importation." });
    }
  };

  // Pagination
  const totalPages = Math.ceil(data.length / rowsPerPage);
  const startIndex = (currentPage - 1) * rowsPerPage;
  const endIndex = startIndex + rowsPerPage;
  const pageData = data.slice(startIndex, endIndex);

  const inputStyle = {
    width: '100%',
    minWidth: '200px',
    border: 'none',
    backgroundColor: 'transparent',
    padding: '12px 16px',
    fontFamily: 'inherit',
    fontSize: 'inherit',
    lineHeight: 'inherit',
    color: 'black',
    outline: 'none',
    boxSizing: 'border-box',
    textAlign: 'left',
  };

  return (
    <div className="min-h-screen bg-gray-100 px-4 py-8">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-4xl font-bold text-center text-green-700 mb-8">Importation Excel</h1>

        {!typeSelected && (
          <div className="bg-white rounded-xl shadow-lg p-8 w-96 mx-auto border-2 border-green-600">
            <h2 className="text-xl font-semibold text-green-700 mb-4 text-center">Sélectionnez le typede projet</h2>
            <div className="flex flex-col gap-3 mb-6">
              {projectTypes.map((type) => (
                <label key={type} className="inline-flex items-center gap-2 cursor-pointer">
                  <input
                    type="checkbox"
                    checked={selectedTypes.includes(type)}
                    onChange={() => toggleType(type)}
                    className="form-checkbox h-5 w-5 text-green-600"
                  />
                  <span className="text-gray-800">{type}</span>
                </label>
              ))}
            </div>
            <button
              onClick={handleValidateTypes}
              className="w-full bg-green-600 hover:bg-green-700 text-white py-3 rounded-lg font-semibold transition"
            >
              Valider
            </button>
          </div>
        )}

        {typeSelected && (
          <>
            {/* Affichage sélection type(s) */}
            <div className="bg-white rounded-xl mt-10 shadow-lg p-6 w-96 mx-auto mb-11 border border-green-500 flex justify-between items-center">
              <div>
                <strong className="text-green-700">Type(s) de projet sélectionné(s) :</strong>{" "}
                {selectedTypes.join(", ")}
              </div>
              <button
                onClick={handleResetTypes}
                className="text-red-600 underline hover:text-red-800"
                title="Changer la sélection"
              >
                Modifier
              </button>
            </div>

            {!file && (
              <div className="bg-white rounded-xl shadow-lg p-10 text-center border-2 border-dashed border-green-600 w-96 mx-auto">
                <FiUpload className="text-green-600 mx-auto mb-4" size={80} />
                <h2 className="text-xl font-semibold text-green-700 mb-2">Déposez un fichier Excel ici</h2>
                <p className="text-gray-600 mb-6">
                  Veillez respecter le format du fichier xlsx. Si vous n'avez pas le bon format, rendez-vous sur "Fonctionnement" pour télécharger.
                </p>
                <label className="inline-block bg-green-600 text-white px-6 py-3 rounded-lg cursor-pointer hover:bg-green-700 transition">
                  Choisir un fichier Excel
                  <input type="file" accept=".xlsx" className="hidden" onChange={handleFileChange} />
                </label>
              </div>
            )}

            {file && (
              <div className="mt-8">
                <div className="text-gray-700 text-center mb-4 italic">Fichier sélectionné : {file.name}</div>

                {/* Tableau Excel */}
                <div className="overflow-auto rounded-lg shadow-md bg-white">
                  <table className="min-w-full text-sm table-auto border-collapse">
                    <thead>
                      <tr className="bg-green-700 text-white">
                        {data[0] &&
                          data[0].map((col, i) => (
                            <th key={i} className="px-4 py-3 text-left font-semibold">
                              {col}
                            </th>
                          ))}
                      </tr>
                    </thead>
                    <tbody>
                      {pageData.slice(1).map((row, rowIndex) => (
                        <tr key={rowIndex} className="even:bg-gray-50 hover:bg-green-50">
                          {row.map((cell, colIndex) => (
                            <td
                              key={colIndex}
                              className="px-4 py-3 border-t border-gray-200 align-top"
                              style={editMode ? { minWidth: '200px' } : {}}
                            >
                              {editMode ? (
                                <input
                                  type="text"
                                  value={cell || ""}
                                  onChange={(e) =>
                                    handleCellChange(startIndex + rowIndex + 1, colIndex, e.target.value)
                                  }
                                  style={inputStyle}
                                  spellCheck={false}
                                />
                              ) : (
                                <span>{cell}</span>
                              )}
                            </td>
                          ))}
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>

                {data.length > rowsPerPage && (
                  <div className="flex justify-center items-center gap-4 mt-6">
                    <button
                      onClick={() => setCurrentPage((p) => Math.max(1, p - 1))}
                      disabled={currentPage === 1}
                      className="bg-gray-200 px-4 py-2 rounded hover:bg-gray-300 disabled:opacity-50"
                    >
                      Précédent
                    </button>
                    <span>
                      Page {currentPage} / {totalPages}
                    </span>
                    <button
                      onClick={() => setCurrentPage((p) => Math.min(totalPages, p + 1))}
                      disabled={currentPage === totalPages}
                      className="bg-gray-200 px-4 py-2 rounded hover:bg-gray-300 disabled:opacity-50"
                    >
                      Suivant
                    </button>
                  </div>
                )}

                {/* Boutons d'action */}
                <div className="flex flex-wrap justify-center gap-4 mt-6">
                  {!editMode && !editFinished && (
                    <>
                      <button
                        onClick={handleEdit}
                        className="bg-yellow-500 hover:bg-yellow-600 text-white px-5 py-2 rounded flex items-center gap-2"
                      >
                        <FiEdit /> Modifier
                      </button>
                      <button
                        onClick={handleImportToDB}
                        className="bg-green-700 hover:bg-green-800 text-white px-5 py-2 rounded flex items-center gap-2"
                      >
                        <FiDatabase /> Importer dans la base
                      </button>
                    </>
                  )}

                  {editMode && (
                    <button
                      onClick={handleFinishEdit}
                      className="bg-blue-600 hover:bg-blue-700 text-white px-5 py-2 rounded flex items-center gap-2"
                    >
                      <FiCheckCircle /> Terminer les modifications
                    </button>
                  )}

                  {!editMode && editFinished && (
                    <>
                      <button
                        onClick={handleSaveCopy}
                        className="bg-gray-700 hover:bg-gray-800 text-white px-5 py-2 rounded flex items-center gap-2"
                      >
                        <FiSave /> Télécharger la copie modifiée
                      </button>
                      <button
                        onClick={handleImportToDB}
                        className="bg-green-700 hover:bg-green-800 text-white px-5 py-2 rounded flex items-center gap-2"
                      >
                        <FiDatabase /> Importer dans la base
                      </button>
                    </>
                  )}
                </div>

                {/* Message résultat importation */}
                {importStatus && (
                  <p
                    className={`mt-6 text-center font-semibold ${
                      importStatus.success ? "text-green-700" : "text-red-700"
                    }`}
                  >
                    {importStatus.message}
                  </p>
                )}
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
}

export default Importation;
