import React from 'react';
import { useNavigate } from 'react-router-dom';
import illustration from '../assets/22.jpg'; // Vérifie que cette image existe

function Home() {
  const navigate = useNavigate();

  return (
    <div className="flex flex-col w-full h-screen m-0 p-0 bg-white overflow-hidden">
      {/* Message d'accueil animé */}
      <div className="w-full text-center mt-6 mb-4">
        <h2 className="text-3xl font-bold text-green-700 animate-slide-in-left">
          Bienvenue sur Excel Import
        </h2>
      </div>

      {/* Bloc principal avec texte et illustration */}
      <div className="flex flex-col md:flex-row items-start justify-center flex-1">
        {/* Texte principal */}
        <div className="md:w-1/2 text-center md:text-left px-6 space-y-6 mt-4">
          <h1 className="text-3xl md:text-4xl font-bold text-green-700 leading-tight">
            Gérez vos données en toute simplicité
          </h1>
          <p className="text-gray-700 text-lg">
            Importez, visualisez et modifiez vos fichiers Excel rapidement. Notre outil vous aide à garder le contrôle sur vos projets de financement.
          </p>
          <div className="flex flex-col sm:flex-row justify-center md:justify-start gap-4">
            <button
              onClick={() => navigate('/comment-ca-marche')}
              className="bg-green-600 text-white px-6 py-3 text-lg rounded-lg hover:bg-white hover:text-black hover:border-solid transition "
            >
              Fonctionnement
            </button>
            <button
              onClick={() => navigate('/importer')}
              className="bg-white border border-green-600 text-green-700 px-6 py-3 text-lg rounded-lg hover:bg-green-100 transition"
            >
              Importer maintenant
            </button>
          </div>
        </div>

        {/* Illustration */}
        <div className="md:w-1/2 mt-10 md:mt-0 flex justify-center px-6">
          <img
            src={illustration}
            alt="Illustration"
            className="w-full max-w-md animate-fade-in-up"
          />
        </div>
      </div>
    </div>
  );
}

export default Home;
