import React from 'react';
import { useNavigate } from 'react-router-dom';
import illustration from '../assets/22.jpg'; // Assurez-vous que ce fichier existe

function Home() {
  const navigate = useNavigate();

  return (
    <div className="relative w-full h-screen overflow-hidden bg-white">
      {/* Message Bienvenue */}
      <div className="absolute top-6 left-1/2 transform -translate-x-1/2 text-center z-10">
        <h1 className="text-2xl md:text-3xl font-semibold">
          Bienvenu sur <span className="text-green-700 font-extrabold">Excel<span className="text-orange-500">Import</span></span>
        </h1>
      </div>

      {/* Contenu principal centré */}
      <div className="flex flex-col md:flex-row items-center justify-center w-full h-full px-6">
        {/* Texte */}
        <div className="md:w-1/2 text-center md:text-left space-y-6 mt-16 md:mt-0">
          <h1 className="text-3xl md:text-4xl font-bold text-green-700 leading-tight">
            Gérez vos données en toute simplicité
          </h1>
          <p className="text-gray-700 text-base md:text-lg font-medium">
            Importez, visualisez et modifiez vos fichiers Excel rapidement. Notre outil vous aide à garder le contrôle sur vos projets de financement.
          </p>
          <div className="flex flex-col sm:flex-row justify-center md:justify-start gap-8 mt-6">
            <button
              onClick={() => navigate('/comment-ca-marche')}
              className="bg-green-600 text-white px-6 py-3 text-base md:text-lg rounded-lg hover:bg-white hover:text-green-700 hover:border transition border-transparent hover:border-green-600"
            >
              Fonctionnement
            </button>
            <button
              onClick={() => navigate('/importer')}
              className="bg-white border border-green-600 text-green-700 px-6 py-3 text-base md:text-lg rounded-lg hover:bg-green-600 hover:text-white transition"
            >
              Importer maintenant
            </button>
          </div>
        </div>

        {/* Illustration */}
        <div className="md:w-1/2 flex justify-center mt-10 md:mt-0">
          <img
            src={illustration}
            alt="Illustration"
            className="max-h-[80vh] w-auto object-contain"
          />
        </div>
      </div>
    </div>
  );
}

export default Home;
