import React, { useState } from 'react';
import { Outlet, NavLink, useNavigate } from 'react-router-dom';
import {
  FiHome, FiHelpCircle, FiUpload, FiClock, FiUser, FiX, FiMenu
} from 'react-icons/fi';
import logo from '../assets/logo.png'; // Ajuste le chemin selon ton projet

function DashboardUserLayout() {
  const navigate = useNavigate();
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  const linkClasses = ({ isActive }) =>
    `flex items-center ${isSidebarOpen ? 'gap-3 px-4 py-3 text-base' : 'justify-center py-3 text-xl'} rounded-lg font-medium transition-all ${
      isActive
        ? 'bg-green-600 text-white shadow'
        : 'text-gray-700 hover:bg-gray-200'
    }`;

  return (
    <div className="flex min-h-screen bg-gray-300 font-sans">
      {/* Sidebar */}
      <aside
        className={`${
          isSidebarOpen ? 'w-64' : 'w-20'
        } bg-white shadow-lg p-4 flex flex-col justify-between transition-all duration-300`}
      >
        <div>
          {/* Toggle */}
          <div className="flex justify-end mb-4">
            <button
              onClick={() => setIsSidebarOpen(!isSidebarOpen)}
              className="text-gray-600 hover:text-gray-900"
            >
              {isSidebarOpen ? <FiX size={20} /> : <FiMenu size={24} />}
            </button>
          </div>

          {/* Logo */}
          <div className="flex justify-center mb-10">
            {isSidebarOpen && (
              <img
                src={logo}
                alt="Logo"
                className="transition-all duration-300 h-24"
              />
            )}
          </div>

          {/* Navigation */}
          <nav className="space-y-4">
            <NavLink to="/" end className={linkClasses}>
              <FiHome /> {isSidebarOpen && 'Accueil'}
            </NavLink>
            <NavLink to="/comment-ca-marche" className={linkClasses}>
              <FiHelpCircle /> {isSidebarOpen && 'Fonctionnement'}
            </NavLink>
            <NavLink to="/importer" className={linkClasses}>
              <FiUpload /> {isSidebarOpen && 'Importer des données'}
            </NavLink>
            <NavLink to="/historique" className={linkClasses}>
              <FiClock /> {isSidebarOpen && 'Historique'}
            </NavLink>
          </nav>
        </div>

        {/* Mon Compte en bas */}
        <NavLink to="/mon-compte" className={linkClasses}>
          <FiUser /> {isSidebarOpen && 'Mon compte'}
        </NavLink>
      </aside>

      {/* Contenu principal */}
      <main className="flex-1 p-10">
        <Outlet />
      </main>
    </div>
  );
}

export default DashboardUserLayout;
