import React from 'react';
import { FiUpload, FiEye, FiDatabase } from 'react-icons/fi';

function CommentCaMarche() {
  const steps = [
    {
      icon: <FiUpload size={28} />,
      title: "1. Importer vos données Excel",
      description: "Choisissez un fichier Excel depuis votre ordinateur ou glissez-déposez-le directement dans l’interface.",
    },
    {
      icon: <FiEye size={28} />,
      title: "2. Prévisualiser et ajuster",
      description: "Vérifiez les données, effectuez des modifications ou supprimez des lignes si nécessaire.",
    },
    {
      icon: <FiDatabase size={28} />,
      title: "3. Enregistrer dans la base",
      description: "Cliquez sur “Importer dans la base” pour sauvegarder vos données dans PostgreSQL.",
    },
  ];

  return (
    <div className="min-h-screen bg-white flex flex-col items-center justify-start py-12 px-6">
      <h1 className="text-4xl md:text-5xl font-bold text-green-700 mb-14 text-center animate-slide-in">
        Comment ça marche ?
      </h1>

      <div className="grid gap-10 max-w-4xl w-full">
        {steps.map((step, index) => (
          <div
            key={index}
            className="flex items-start gap-6 bg-green-50 hover:bg-green-100 shadow-md hover:shadow-lg rounded-xl p-6 transition-all duration-300"
          >
            <div className="bg-green-600 text-white p-3 rounded-full shadow-md">
              {step.icon}
            </div>
            <div>
              <h2 className="text-xl font-semibold text-green-700 mb-1">{step.title}</h2>
              <p className="text-gray-700 leading-relaxed">{step.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CommentCaMarche;
