import React from 'react';
import { useNavigate } from 'react-router-dom';
import illustration from '../assets/dec.png'; // Vérifie que cette image existe

function Home() {
  const navigate = useNavigate();

  return (
    <div className="flex flex-col md:flex-row items-center justify-between  min-h-screen m-0 px-10 py-16 bg-gradient-to-br bg-gray-100 from-white ">
      {/* Texte principal */}
      <div className="md:w-1/2 space-y-6">
        <h1 className="text-3xl font-bold text-green-700 leading-tight">
          Gérez vos données<br />en toute simplicité
        </h1>
        <p className="text-gray-700 text-xl">
          Importez, visualisez et modifiez vos fichiers Excel rapidement. Notre outil vous aide à garder le contrôle sur vos projets de financement.
        </p>
        <button
          onClick={() => navigate('/importer')}
          className="bg-green-600 text-white px-6 py-3 text-lg rounded-lg hover:bg-green-700 transition"
        >
          Importer maintenant
        </button>
      </div>

      {/* Illustration */}
      <div className="md:w-1/2 mt-10 md:mt-0 flex justify-center">
        <img
          src={illustration}
          alt="Illustration"
          className="w-full max-w-lg animate-fade-in-up"
        />
      </div>
    </div>
  );
}

export default Home;
