import React, { useState } from 'react';
import { FiUploadCloud, FiFileText, FiArrowRightCircle } from 'react-icons/fi';

function Importation() {
  const [file, setFile] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleImport = () => {
    if (file) {
      console.log('Fichier importé :', file.name);
      // Appel API backend ici
    } else {
      alert("Veuillez sélectionner un fichier Excel.");
    }
  };

  return (
    <div className="min-h-screen w-full bg-gradient-to-br from-white to-green-50 flex items-center justify-center px-6 py-12">
      <div className="w-full max-w-6xl bg-white shadow-2xl rounded-3xl overflow-hidden grid grid-cols-1 md:grid-cols-2">
        {/* Illustration */}
        <div className="hidden md:flex flex-col justify-center items-center bg-green-100 px-6 py-12">
          <img
            src="https://cdn-icons-png.flaticon.com/512/3022/3022256.png"
            alt="Upload illustration"
            className="w-72 h-auto mb-4 animate-fade-in"
          />
          <p className="text-green-800 font-semibold text-center">
            Importez vos fichiers Excel pour lancer l’analyse de vos données.
          </p>
        </div>

        {/* Formulaire */}
        <div className="flex flex-col items-center justify-center p-8 md:p-12 space-y-6">
          <h1 className="text-3xl font-bold text-green-700 text-center">
            Importation de données
          </h1>
          <p className="text-gray-600 text-center max-w-sm">
            Glissez-déposez un fichier <strong>.xlsx</strong> ou sélectionnez-en un.
            Le fichier doit respecter la structure attendue.
          </p>

          <label className="cursor-pointer bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded-xl font-medium transition flex items-center gap-2">
            <FiUploadCloud size={22} />
            Choisir un fichier Excel
            <input type="file" accept=".xlsx" className="hidden" onChange={handleFileChange} />
          </label>

          {file && (
            <div className="flex items-center gap-2 text-gray-700">
              <FiFileText />
              <span className="truncate max-w-[200px]">{file.name}</span>
            </div>
          )}

          <button
            onClick={handleImport}
            className="mt-4 bg-green-700 hover:bg-green-800 text-white px-6 py-3 rounded-xl font-semibold flex items-center gap-2 transition"
          >
            Importer <FiArrowRightCircle />
          </button>
        </div>
      </div>
    </div>
  );
}

export default Importation;
