import React, { useState } from 'react';
import { FiUploadCloud, FiFileText, FiEdit, FiCheckCircle, FiSave, FiDatabase } from 'react-icons/fi';
import * as XLSX from 'xlsx';

function Importation() {
  const [file, setFile] = useState(null);
  const [data, setData] = useState([]);
  const [editMode, setEditMode] = useState(false);
  const [editFinished, setEditFinished] = useState(false);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);

    const reader = new FileReader();
    reader.onload = (evt) => {
      const bstr = evt.target.result;
      const wb = XLSX.read(bstr, { type: "binary" });
      const wsname = wb.SheetNames[0];
      const ws = wb.Sheets[wsname];
      const jsonData = XLSX.utils.sheet_to_json(ws, { header: 1 });
      setData(jsonData);
    };
    reader.readAsBinaryString(selectedFile);
  };

  const handleEdit = () => {
    setEditMode(true);
  };

  const handleFinishEdit = () => {
    setEditMode(false);
    setEditFinished(true);
  };

  const handleCellChange = (rowIndex, colIndex, value) => {
    const newData = [...data];
    newData[rowIndex][colIndex] = value;
    setData(newData);
  };

  const handleSaveCopy = () => {
    const ws = XLSX.utils.aoa_to_sheet(data);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Feuille1");
    XLSX.writeFile(wb, "donnees_modifiees.xlsx");
  };

  const handleImportToDB = () => {
    console.log("Importation vers la base de données...");
    // Tu peux ici appeler le backend Flask avec les données `data`
  };

  return (
    <div className="min-h-screen bg-white p-6 flex flex-col items-center">
      <h1 className="text-3xl font-bold text-green-700 mb-6">Importation de fichier Excel</h1>

   {!file && (
  <div className="w-full max-w-xl p-8 bg-green-50 border-2 border-dashed border-green-400 rounded-2xl shadow-md text-center transition hover:shadow-lg hover:bg-green-100 mb-10">
    <FiUpload className="mx-auto text-green-600" size={50} />
    <h2 className="text-xl font-semibold text-green-700 mt-4 mb-2">Déposez votre fichier Excel ici</h2>
    <p className="text-gray-600 mb-4">ou cliquez pour en sélectionner un sur votre ordinateur</p>

    <label className="cursor-pointer inline-block bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded-lg font-medium transition">
      Prendre un fichier Excel
      <input type="file" accept=".xlsx" className="hidden" onChange={handleFileChange} />
    </label>

    <p className="text-sm text-gray-500 mt-4 italic">
      Format accepté : <strong>.xlsx</strong> – veillez à respecter la structure demandée
    </p>
  </div>
)}


      {file && (
        <div className="w-full mt-6 flex flex-col items-center">
          <div className="mb-4 text-center text-gray-600">
            <FiFileText className="inline-block mr-2" /> {file.name}
          </div>

          <div className="overflow-auto max-w-full mb-4">
            <table className="table-auto border border-gray-300 text-sm">
              <tbody>
                {data.map((row, rowIndex) => (
                  <tr key={rowIndex}>
                    {row.map((cell, colIndex) => (
                      <td key={colIndex} className="border px-3 py-1">
                        {editMode ? (
                          <input
                            value={cell}
                            onChange={(e) => handleCellChange(rowIndex, colIndex, e.target.value)}
                            className="w-full border-b border-gray-400 focus:outline-none"
                          />
                        ) : (
                          <span>{cell}</span>
                        )}
                      </td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {!editMode && !editFinished && (
            <div className="flex gap-4">
              <button
                onClick={handleEdit}
                className="bg-yellow-500 hover:bg-yellow-600 text-white px-4 py-2 rounded flex items-center gap-2"
              >
                <FiEdit /> Modifier
              </button>
              <button
                onClick={handleImportToDB}
                className="bg-green-700 hover:bg-green-800 text-white px-4 py-2 rounded flex items-center gap-2"
              >
                <FiDatabase /> Importer dans la base
              </button>
            </div>
          )}

          {editMode && (
            <button
              onClick={handleFinishEdit}
              className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded flex items-center gap-2"
            >
              <FiCheckCircle /> Terminer
            </button>
          )}

          {!editMode && editFinished && (
            <div className="flex gap-4 mt-4">
              <button
                onClick={handleSaveCopy}
                className="bg-gray-700 hover:bg-gray-800 text-white px-4 py-2 rounded flex items-center gap-2"
              >
                <FiSave /> Enregistrer une copie
              </button>
              <button
                onClick={handleImportToDB}
                className="bg-green-700 hover:bg-green-800 text-white px-4 py-2 rounded flex items-center gap-2"
              >
                <FiDatabase /> Importer dans la base
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default Importation;
