import React from 'react';
import { FiUser, FiMail, FiLock } from 'react-icons/fi';

function MonCompte() {
  const username = "Utilisateur";
  const email = "utilisateur@example.com";

  return (
    <div className="w-full h-screen bg-white flex flex-col items-center justify-start p-6 overflow-auto">
      {/* Titre */}
      <h2 className="text-3xl md:text-4xl font-bold text-green-700 mb-10 animate-slide-in-left">
        Mon compte
      </h2>

      <div className="w-full max-w-xl space-y-6">
        {/* Nom utilisateur */}
        <div className="flex items-center gap-4 p-4 bg-gray-100 rounded-lg shadow">
          <FiUser className="text-green-600" size={24} />
          <div>
            <p className="text-sm text-gray-500">Nom dâ€™utilisateur</p>
            <p className="text-lg font-semibold">{username}</p>
          </div>
        </div>

        {/* Email */}
        <div className="flex items-center gap-4 p-4 bg-gray-100 rounded-lg shadow">
          <FiMail className="text-green-600" size={24} />
          <div>
            <p className="text-sm text-gray-500">Email</p>
            <p className="text-lg font-semibold">{email}</p>
          </div>
        </div>

        {/* Bouton Modifier mot de passe */}
        <div className="flex items-center gap-4 p-4 bg-gray-100 rounded-lg shadow">
          <FiLock className="text-green-600" size={24} />
          <div>
            <p className="text-sm text-gray-500">Mot de passe</p>
            <button className="text-green-600 hover:underline font-medium">
              Modifier mon mot de passe
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default MonCompte;
