import React from 'react';
import { FiUpload, FiEye, FiDatabase } from 'react-icons/fi';

function CommentCaMarche() {
  const steps = [
    {
      icon: <FiUpload size={28} />,
      title: "Importer vos données Excel",
      description: "Glissez-déposez ou sélectionnez un fichier Excel (.xlsx) depuis votre appareil.",
    },
    {
      icon: <FiEye size={28} />,
      title: "Prévisualisez et ajustez",
      description: "Modifiez, corrigez ou supprimez les lignes avant de valider l'importation.",
    },
    {
      icon: <FiDatabase size={28} />,
      title: "Enregistrez dans la base",
      description: "Une fois prêt, enregistrez vos données dans la base PostgreSQL sécurisée.",
    },
  ];

  return (
    <div className="min-h-screen bg-white flex flex-col items-center justify-start py-12 px-6">
      <h1 className="text-4xl font-bold text-green-700 mb-12 text-center">
        Comment ça marche ?
      </h1>

      <div className="max-w-3xl w-full space-y-10">
        {steps.map((step, index) => (
          <div key={index} className="flex items-start gap-6">
            <div className="bg-green-100 text-green-700 p-4 rounded-full">
              {step.icon}
            </div>
            <div>
              <h2 className="text-xl font-semibold mb-1">{step.title}</h2>
              <p className="text-gray-600">{step.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CommentCaMarche;
