import React, { useState, useRef, useEffect } from "react";
import { FiUser, FiMail, FiLock, FiEdit3, FiCheck, FiX, FiEye, FiEyeOff, FiImage } from "react-icons/fi";

function MonCompte() {
  const [nom, setNom] = useState("");
  const [prenom, setPrenom] = useState("");
  const [email, setEmail] = useState("");
  const [editField, setEditField] = useState(null);
  const [passwordFields, setPasswordFields] = useState({
    current: "",
    new: "",
    confirm: "",
  });
  const [showPassword, setShowPassword] = useState({
    current: false,
    new: false,
    confirm: false,
  });
  const [tempValues, setTempValues] = useState({
    nom: "",
    prenom: "",
    email: "",
  });
  // Avatar (image base64)
  const [avatar, setAvatar] = useState(null);
  const [isCameraOpen, setIsCameraOpen] = useState(false);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  // Chargement profil au montage
  useEffect(() => {
    fetch("/profile", {
      method: "GET",
      credentials: "include",
    })
      .then((res) => {
        if (!res.ok) throw new Error("Impossible de charger le profil utilisateur.");
        return res.json();
      })
      .then((data) => {
        setNom(data.nom);
        setPrenom(data.prenom);
        setEmail(data.email);
        setTempValues({
          nom: data.nom,
          prenom: data.prenom,
          email: data.email,
        });
        if (data.avatar) setAvatar(data.avatar);
      })
      .catch((err) => {
        alert(err.message);
      });
  }, []);

  // Gestion édition champs
  const handleEditClick = (field) => {
    setEditField(field);
    setTempValues((prev) => ({ ...prev, [field]: eval(field) }));
  };

  const handleCancel = () => {
    setEditField(null);
    setTempValues({
      nom,
      prenom,
      email,
    });
  };

  const handleChange = (field, value) => {
    setTempValues((prev) => ({ ...prev, [field]: value }));
  };

  const handleSave = () => {
    // Ici, on envoie les données modifiées au backend
    const dataToSend = {
      nom: tempValues.nom,
      prenom: tempValues.prenom,
      email: tempValues.email,
      avatar: avatar,
      password: null, // gestion mot de passe plus bas
    };

    fetch("/profile", {
      method: "POST",
      credentials: "include",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(dataToSend),
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erreur lors de la sauvegarde.");
        return res.json();
      })
      .then((data) => {
        setNom(tempValues.nom);
        setPrenom(tempValues.prenom);
        setEmail(tempValues.email);
        setEditField(null);
        alert("Profil mis à jour avec succès !");
      })
      .catch((err) => {
        alert(err.message);
      });
  };

  // Gestion mot de passe (champ caché / affiché)
  const toggleShowPassword = (field) => {
    setShowPassword((prev) => ({ ...prev, [field]: !prev[field] }));
  };

  const handlePasswordChange = (field, value) => {
    setPasswordFields((prev) => ({ ...prev, [field]: value }));
  };

  // Gestion caméra avatar
  const startCamera = () => {
    setIsCameraOpen(true);
    if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
      navigator.mediaDevices
        .getUserMedia({ video: true })
        .then((stream) => {
          videoRef.current.srcObject = stream;
          videoRef.current.play();
        })
        .catch(() => alert("Impossible d'accéder à la caméra."));
    }
  };

  const takePhoto = () => {
    const context = canvasRef.current.getContext("2d");
    context.drawImage(videoRef.current, 0, 0, 320, 240);
    const imageData = canvasRef.current.toDataURL("image/png");
    setAvatar(imageData);
    stopCamera();
  };

  const stopCamera = () => {
    setIsCameraOpen(false);
    if (videoRef.current && videoRef.current.srcObject) {
      videoRef.current.srcObject.getTracks().forEach((track) => track.stop());
      videoRef.current.srcObject = null;
    }
  };

  return (
    <div className="mon-compte-container p-4 max-w-lg mx-auto bg-white rounded shadow">
      <h2 className="text-xl font-semibold mb-4">Mon compte</h2>

      {/* Avatar */}
      <div className="avatar-section mb-4 text-center">
        {avatar ? (
          <img src={avatar} alt="Avatar" className="w-24 h-24 rounded-full mx-auto mb-2 object-cover" />
        ) : (
          <FiUser className="w-24 h-24 mx-auto mb-2 text-gray-400" />
        )}
        {!isCameraOpen ? (
          <button
            className="btn btn-primary inline-flex items-center gap-2"
            onClick={startCamera}
            title="Prendre une photo"
          >
            <FiImage /> Prendre une photo
          </button>
        ) : (
          <div>
            <video ref={videoRef} width="320" height="240" className="mx-auto rounded"></video>
            <div className="flex justify-center gap-4 mt-2">
              <button onClick={takePhoto} className="btn btn-success">
                <FiCheck /> Capturer
              </button>
              <button onClick={stopCamera} className="btn btn-danger">
                <FiX /> Annuler
              </button>
            </div>
            <canvas ref={canvasRef} width="320" height="240" style={{ display: "none" }} />
          </div>
        )}
      </div>

      {/* Champs Nom, Prenom, Email */}
      {["nom", "prenom", "email"].map((field) => (
        <div key={field} className="mb-4">
          <label className="block font-medium mb-1 capitalize">{field}</label>
          {editField === field ? (
            <div className="flex items-center gap-2">
              <input
                type={field === "email" ? "email" : "text"}
                value={tempValues[field]}
                onChange={(e) => handleChange(field, e.target.value)}
                className="border rounded px-2 py-1 flex-grow"
              />
              <button onClick={handleSave} className="btn btn-success" title="Enregistrer">
                <FiCheck />
              </button>
              <button onClick={handleCancel} className="btn btn-danger" title="Annuler">
                <FiX />
              </button>
            </div>
          ) : (
            <div className="flex items-center justify-between border rounded px-2 py-1 cursor-pointer" onClick={() => handleEditClick(field)}>
              <span>{eval(field)}</span>
              <FiEdit3 className="text-gray-500" />
            </div>
          )}
        </div>
      ))}

      {/* Gestion mot de passe */}
      <div className="password-section mt-6">
        <h3 className="text-lg font-semibold mb-2">Changer le mot de passe</h3>

        {["current", "new", "confirm"].map((field) => (
          <div key={field} className="mb-3">
            <label className="block font-medium mb-1 capitalize">
              {field === "current"
                ? "Mot de passe actuel"
                : field === "new"
                ? "Nouveau mot de passe"
                : "Confirmer le mot de passe"}
            </label>
            <div className="relative">
              <input
                type={showPassword[field] ? "text" : "password"}
                value={passwordFields[field]}
                onChange={(e) => handlePasswordChange(field, e.target.value)}
                className="border rounded px-2 py-1 w-full"
              />
              <button
                type="button"
                onClick={() => toggleShowPassword(field)}
                className="absolute right-2 top-1/2 transform -translate-y-1/2 text-gray-600"
                tabIndex={-1}
                aria-label={showPassword[field] ? "Cacher le mot de passe" : "Afficher le mot de passe"}
              >
                {showPassword[field] ? <FiEyeOff /> : <FiEye />}
              </button>
            </div>
          </div>
        ))}

        {/* Ici tu peux ajouter un bouton pour valider le changement de mot de passe, à gérer en backend */}
        <button className="btn btn-primary mt-2" onClick={() => alert("Gestion changement mot de passe à implémenter")}>
          Modifier le mot de passe
        </button>
      </div>
    </div>
  );
}

export default MonCompte;
